mapper.update:
UPDATE
	GIBU.TMOB_CONTENT_LIST
SET
	BRAN_CD = # { branCd }
WHERE
	STAFF_CD = # { staffCd }
	AND BRAN_CD <> # { branCd }

UPDATE
	GIBU.TMOB_CONTENT_LIST
SET
	BRAN_CD = # { branCd }
WHERE
	STAFF_CD = # { staffCd }
	AND BRAN_CD <> # { branCd }

mapper.select:
SELECT
	DONG
FROM
	GIBU.TBRA_STAFF_ZONECHG_dong
WHERE
	BRAN_CD = # { branCd }
	AND CHG_DAY = # { chgDay }
	AND CHG_NUM = # { chgNum }
ORDER BY
	DONG
