	this.tab_UpsoInfo.UPSO.tab_Onwer.PERMMSTR.edt_PERMMSTR_RESINUM2.Text = "";
	this.tab_UpsoInfo.UPSO.tab_Onwer.PERMMSTR.edt_PERMMSTR_ZIP1.Text = "";
