/*
 * 기      능: 조회의 업소이름 클릭 시 특수문자 제한
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function edt_UPSO_CD_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	var strtext = right(strPostText,1);
	var chkstr = "[<!#$%*&_:=|`~'"+Quote("")+";@^+\\>-]"; 
	
	if(pos(chkstr, strtext) >= 0)   // 특수문자 사용 제한
	{
		return false;
	}
	
	if (length(strPostText) == 8 && nChar >= asc('a') && nChar <= asc('z')) {
		obj.Text = strPreText + toUpper(chr(nChar));
		return false;
	}
}

/*
 * 기      능: 업소 전화번호 입력제한
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function tab_UpsoInfo_UPSO_edt_UPSO_PHON_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
   return check_phone_number(strPostText);
}

/*
 * 기      능: 업소명에 키보드 입력시
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function tab_UpsoInfo_UPSO_edt_UPSO_NM_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
//업소코드 입력 조회시 바로 조회창 페이지를 거치지 않고 바로 검색
	if(this.cob_BRAN_CD.Value==null){
	
        gfn_Confirm("MB_OK", "지부를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}
	
	if(savegbn = "Y"){
		var yesno = gfn_Confirm("MB_YESNO", "신규내용을 입력하지 않고, 조회를 하시겠습니까 ?", "경고", "0");
		if (yesno == "6") {
			cob_BRAN_CD_OnChanged();
			this.edt_UPSO_CD.SetFocus();
			savegbn = "N";
			this.edt_upso_cd.BKColor = "user13";
		}
		else {
			this.tab_UpsoInfo.UPSO.edt_UPSO_NM.SetFocus();
		}
	}
	
	if (nChar == 13 and savegbn !="Y") {
		fn_GetUPSO_CD_cd();
	}
	else if (nChar == 112 and savegbn !="Y") {
		fn_FindUPSO();
	}
	return;

}

/*
 * 기      능: 업소코드에 키보드 입력시
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function edt_UPSO_CD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	//업소코드 입력 조회시 바로 조회창 페이지를 거치지 않고 바로 검색
	if(this.cob_BRAN_CD.Value==null){
	
        gfn_Confirm("MB_OK", "지부를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}
	
	if(savegbn = "Y"){
		var yesno = gfn_Confirm("MB_YESNO", "신규내용을 입력하지 않고, 조회를 하시겠습니까 ?", "경고", "0");
		if (yesno == "6") {
			cob_BRAN_CD_OnChanged();
			this.edt_UPSO_CD.SetFocus();
			savegbn = "N";
			this.edt_upso_cd.BKColor = "user13";
		}
		else {
			this.tab_UpsoInfo.UPSO.edt_UPSO_NM.SetFocus();
		}
	}
	
	if (nChar == 13 && savegbn !="Y") {
		fn_GetUPSO_CD_cd();
	}
	else if (nChar == 112 && savegbn !="Y") {
		fn_FindUPSO();
	}
	return;

}

/*
 * 기      능: 계산서 발행시 체크시 거래처 고드 활성화
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function tab_UpsoInfo_UPSO_chk_BILL_ISS_YN_OnClick(obj,strValue)
{
	if(this.tab_UpsoInfo.UPSO.chk_BILL_ISS_YN.Value==1){
		this.tab_UpsoInfo.UPSO.edt_bscon_Cd.BKColor =  'user13';
	}
	else {
		this.tab_UpsoInfo.UPSO.edt_bscon_Cd.BKColor = "";
	}
		
}

