function upso_regist_OnFocus(obj)
{
	if(length(g_bran_cd) > 0 && length(g_upso_cd) > 0 ){
		
        this.cob_BRAN_CD.Value = g_bran_cd;
		this.edt_UPSO_CD.Text = g_upso_cd;
		
		this.ds_BRAN_CD.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.value);
		gfn_syncCall("SAWON_CLICK", "KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select", 
		"S=ds_BRAN_CD","ds_SawonInfo=SEL1","","fn_CallBack");
	
		fn_search();
    }
}

/*
 * 기      능: 업소중복 체크
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function tab_UpsoInfo_UPSO_btn_Juso_jungbok_OnClick(obj,nX,nY)
{
	var UPSO_ADDR1 = this.tab_UpsoInfo.UPSO.edt_UPSO_ADDR1.Text;
	var UPSO_ADDR2 = this.tab_UpsoInfo.UPSO.edt_UPSO_ADDR2.Text;
	var inParam ="UPSO_ADDR1=" + quote(UPSO_ADDR1) + " UPSO_ADDR2=" + quote(UPSO_ADDR2) ;
	var result = Dialog("fi_bra::chk_Jungbok2_t.xml",inParam);
}

/*
 * 기      능: 업소중복 체크
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
*/
function actbution(){
	this.tab_UpsoInfo.UPSO.btn_FindZip.Enable		= true;					//업소 정보 -> 업소 주소 버튼
	this.tab_UpsoInfo.UPSO.btn_FindUpjong.Enable	= true;					//사용료정보 -> 업종선탣 ->조회버튼
	this.tab_UpsoInfo.UPSO.edt_UPSOGRAD.Enable      = true;
	this.tab_UpsoInfo.UPSO.tab_Onwer.MNGEMSTR.btn_FindMZip.Enable = true;	//업주정보 -> 자택주소정보
	this.tab_UpsoInfo.UPSO.btn_code.Enable 			= true;					//징수정보 -> 거래처코드 버튼
	
}



