function tab_UpsoInfo_UPSO_edt_UPSO_CD_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	var strtext = right(strPostText,1);
	var chkstr = "[<!#$%*&_:=|`~'"+Quote("")+";@^+\\>-]"; 
	
	if(pos(chkstr, strtext) >= 0)   // 특수문자 사용 제한
	{
		return false;
	}
	
	if (length(strPostText) == 8 && nChar >= asc('a') && nChar <= asc('z')) {
		obj.Text = strPreText + toUpper(chr(nChar));
		return false;
	}
}

function tab_UpsoInfo_UPSO_edt_bscon_Cd_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		var bscon_cd = this.tab_UpsoInfo.UPSO.edt_bscon_Cd.Text;
		if (length(bscon_cd) > 0) {
			this.ds_TLEV_BSCON_SC.SetColumn(0, "BSCON_CD", bscon_cd);

			gfn_SyncCall("svcBscon", "KOMCA?SYSID=PATHFINDER&MENUID=1000001001&EVENTID=bscon_select", "S=ds_TLEV_BSCON_SC",
					"ds_TLEV_BSCON=SEL1", "", "fn_CallBack");
					
			if (ds_TLEV_BSCON.rowcount == 1) {
				this.tab_UpsoInfo.UPSO.edt_bscon_Nm.Text = ds_TLEV_BSCON.GetColumn(0, "BSCONHAN_NM");
			}
			else {
				this.tab_UpsoInfo.UPSO.edt_bscon_Nm.Text = "";
			}
		}
		else {
			this.tab_UpsoInfo.UPSO.edt_bscon_Nm.Text = "";
		}
	}
	else if (nChar == 112) {
		Dialog("fi_pub::com01_r02.xml", "formType=1 bsconCd=this.tab_UpsoInfo.UPSO.edt_bscon_cd bsconNm=tab_UpsoInfo.UPSO.edt_bscon_Nm ", "");
	}
}

function tab_UpsoInfo_UPSO_edt_bscon_Cd_OnChanged(obj,strText)
{
	if (length(strText) == 0) this.tab_UpsoInfo.UPSO.edt_bscon_Nm.Text = "";
}

function tab_UpsoInfo_UPSO_edt_UPSOGRAD_OnChanged(obj,strText)
{
	this.ds_UpsoInfo.SetColumn(0, "MONPRNCFEE", toNumber(this.tab_UpsoInfo.UPSO.mae_MONPRNCFEE.Value));
}

function tab_UpsoInfo_UPSO_edt_UPSOGRAD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		var result = gfn_GetGRAD_CD(obj.Text);
		fn_ProcGrad(result);
	}
	else if (nChar == 112) {
		tab_UpsoInfo_UPSO_btn_FindUpjong_OnClick(obj);
	}
}

function check_phone_number(strPostText)
{	
	//ALERT(strPostText+",len:"+length(strPostText));
	if(length(strPostText) !=0){
		var strtext = right(strPostText,1);
		//var chkstr = "[<!#$%*&_:=|`~,. '"+Quote("")+";@^+\\>]"; 
		var chkstr = "1234567890-"; 
		
		if(pos(chkstr, strtext) == -1)   // 특수문자 사용 제한
		{
		   //alert("이름 " + strtext +"(은)는 사용할수 없습니다.");
			return false;
		}
		
		if((asc(right(strPostText,1)) >= 65) && (asc(right(strPostText,1)) <= 122))
		{
		   // alert("영문은 입력불가");
			return false;
		}
	}
}	

function tab_UpsoInfo_UPSO_tab_Onwer_PERMMSTR_edt_PERMMSTR_PHONNUM_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	return check_phone_number(strPostText);
}

function tab_UpsoInfo_UPSO_tab_Onwer_PERMMSTR_edt_PERMMSTR_HPNUM_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	return check_phone_number(strPostText);
}

function tab_UpsoInfo_UPSO_tab_Onwer_MNGEMSTR_edt_MNGEMSTR_HPNUM_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	return check_phone_number(strPostText);
}

function tab_UpsoInfo_UPSO_tab_Onwer_MNGEMSTR_edt_MNGEMSTR_PHONNUM_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	return check_phone_number(strPostText);
}
function tab_UpsoInfo_UPSO_mae_USE_TIME_OnCharChanged(obj,strPreText,strPostText)
{
	var v_use_time = this.tab_UpsoInfo.UPSO.mae_USE_TIME.value;

	if (toNumber(v_use_time) < 1 || toNumber(v_use_time) > 15) 
	{
		gfn_Confirm("MB_OK", "에어로빅, 노래교실, 무도장의 사용시간은 1~15사이의 값을 입력하십시요", "경고", "0");
		return;
	}

	var result = gfn_GetGRAD_CD(this.tab_UpsoInfo.UPSO.edt_UPSOGRAD.Text);

	if (length(result) > 0) {		var vArr = result.split(",");

		var v_new_fee = Truncate(toNumber(vArr[2]) * toNumber(v_use_time) / 15);
		v_new_fee = truncate(v_new_fee / 10) * 10;	//원단위절사

		this.tab_UpsoInfo.UPSO.mae_NMONPRNCFEE.Value = v_new_fee;
	}
}

function tab_UpsoInfo_UPSO_tab_Onwer_AUTO_INFO_cob_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 'e') {
		this.tab_UpsoInfo.UPSO.tab_Onwer.AUTO_INFO.edt_email.Enable = true;
	} else {
		this.tab_UpsoInfo.UPSO.tab_Onwer.AUTO_INFO.edt_email.Text = "";
		this.tab_UpsoInfo.UPSO.tab_Onwer.AUTO_INFO.edt_email.Enable = false;
	}
}
