function upso_visit_OnLoadCompleted(obj)
{
//업소이름을 구하기 위한 인자
	this.ds_NM_In.AddRow();
	gfn_loadForm(obj);
	
	// 지부코드 가져오기
	gfn_GetDataGIBU_CD(this.cob_BRAN_CD, 1);
	
	cal_StartDate.Value = "19990101" ;
	cal_EndDate.Value = today();
	
	this.tab_Onwer.MNGEMSTR.cob_match_yn.Value ='M';

	// 설정 코드 정보 가져오기
	gfn_PubCode("ds_gbn", 2, "00198");		//구분
	ds_gbn.Filter("CODE_ETC=\"G\"");

	gfn_SetPermission(this.cob_BRAN_CD.value);
    gfn_SetInnerPermission("tab_Onwer.MNGEMSTR.btn_print,tab_Onwer.MNGEMSTR.btn_play,tab_Onwer.MNGEMSTR.btn_add,tab_Onwer.MNGEMSTR.btn_del");
	
}

/*
	공통버튼 컨트롤
