function fn_CommmonButtonClick(strCode)
{
    if(strCode == "NEW")
    {
		fn_New();
	}
    else if(strCode == "SEARCH")
    {
		fn_Search();
	}
    else if(strCode == "SAVE")
    {
    	fn_Save();
    }
    else if(strCode == "DELETE")
    {
    	fn_Delete();
    }
    else if(strCode == "EXCEL")
    {
    	fn_Excel();
    }
    else if(strCode == "PRINT")
    {
    	fn_Print();
    }
    else if(strCode == "CLOSE")
    {
        close();
    }
}

function fn_CallBack(svcid,errCode,errMsg) 
{
	if (errCode = 0)
	{
		switch(svcid)
		{
			case "svcSearch" :
				break;
			case "svcDelete" :
				alert("삭제되었습니다.");
				fn_Search();
				break;
			case "svcStaff" :
				ds_SawonInfo.InsertRow(0);
				ds_SawonInfo.SetColumn(0,"STAFF_CD","");
				ds_SawonInfo.SetColumn(0,"STAFF_NM","--ALL--");

				cob_Staff_Nm.Value = gds_sessioninfo.GetColumn(0, "STAFF_NUM");
				if(cob_Staff_Nm.Index < 0)
				{
					cob_Staff_Nm.Index = 0;
				}
				break;
			default: break;
		}
	}
}

function bra01_s28_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);

	bra01_s28_OnSize();

	cob_Regist_Yn.Index = 2;
	cal_Sday.Value = today();

	gfn_syncCall("svcAuth","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008008&EVENTID=sel_udtkpres","","ds_Auth=SEL1","","fn_CallBack");

/*	if(ds_Auth.FindRow("UDTKPRES_ID", gds_sessioninfo.GetColumn(0, "STAFF_NUM")) > 0)
	{
		this.cob_Staff_Nm.Enable = true;
	}
	else
	{
		this.cob_Staff_Nm.Enable = false;
	}*/

	// 센터코드 가져오기
	gfn_GetDataGIBU_CD(cob_BRAN_CD, 0);
	gfn_syncCall("find_GIBU","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=c_gibu_select","","ds_GIBU_CD=SEL1","","fn_CallBack");

	cob_BRAN_CD_OnChanged(this, cob_BRAN_CD.Value, "", "", "");
}

function cob_BRAN_CD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	gfn_SetPermission(strCode);

	this.ds_BRAN_CD.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.value);
	gfn_syncCall("svcStaff","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select","S=ds_BRAN_CD","ds_SawonInfo=SEL1","","fn_CallBack");
}

function fn_New()
{
}

function fn_Search()
{
	if(this.cob_BRAN_CD.Value==null )
	{
        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}

	gfn_syncCall("svcSearch","KOMCA?SYSID=PATHFINDER&MENUID=1000001006004028&EVENTID=sel_new_upso","S=ds_Search","ds_New_Upso=SEL1","","fn_CallBack");
}

function fn_Save()
{
}

function fn_Delete()
{
	if(   (   (   ds_New_Upso.GetColumn(ds_New_Upso.currow, "STAFF_CD") == gds_sessioninfo.GetColumn(0, "STAFF_NUM")
	           || ds_New_Upso.GetColumn(ds_New_Upso.currow, "INSPRES_ID") == gds_sessioninfo.GetColumn(0, "STAFF_NUM"))
	       && ds_New_Upso.GetColumn(ds_New_Upso.currow, "GBN") == "N")
	   || gds_sessioninfo.GetColumn(0, "BRAN_CD") == "AL")
	{
		if(Confirm(ds_New_Upso.GetColumn(ds_New_Upso.currow, "UPSO_NM") + " 업소를 삭제하시겠습니까?"))
		{
			ds_New_Upso_del.ClearData();
			ds_New_Upso_del.AddRow();
			ds_New_Upso_del.SetColumn(0, "UPSO_CD", ds_New_Upso.GetColumn(ds_New_Upso.currow, "UPSO_CD"));
			ds_New_Upso_del.SetColumn(0, "BRAN_CD", ds_New_Upso.GetColumn(ds_New_Upso.currow, "BRAN_CD"));
			gfn_syncCall("svcDelete","KOMCA?SYSID=PATHFINDER&MENUID=1000001006004028&EVENTID=del_new_upso","S=ds_New_Upso_del","","","fn_CallBack");
		}
	}
	else
	{
		alert("삭제할 수 없는 업소목록 입니다.");
	}
}

function fn_Excel()
{
	grd_New_Upso.ExportExcel(cob_Regist_Yn.Text + " 신규업소");
}

