function grd_New_Upso_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(grd_New_Upso.GetClickedCell() == grd_New_Upso.GetBindCellIndex("body", "ATTCH") && length(ds_New_Upso.GetColumn(nRow, "FILE_NM")) > 0)
	{
		gfn_fileDownLoad(ds_New_Upso.GetColumn(ds_New_Upso.currow, "FILE_ROUT"), ds_New_Upso.GetColumn(ds_New_Upso.currow, "FILE_NM"), File0, FileDialog0);
	}
}

function bra01_s28_OnSize(obj,nCx,nCy,nState)
{
	gfn_ObjreSize(Shape0, this, 1, 0);
	gfn_ObjreSize(grd_New_Upso, this, 1, 0);
}
