/*
 * 기      능: 공통버튼 제어
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
 */
function fn_CommmonButtonClick(strCode)
{
    if (strCode == "NEW")	///신규    
    {
		ds_TLEV_USEAPPRV.DeleteAll();
		ds_TLEV_APPTNPRESINFO.DeleteAll();
		ds_TLEV_CLRREC.DeleteAll();
		ds_TLEV_USEDEMDREPT.DeleteAll();
		ds_TLEV_USEQTY.DeleteAll();
		ds_TLEV_USEWRTG.DeleteAll();

		rowCnt = ds_TLEV_USEAPPRV.AddRow();
		edt_Appnum.Enable = false;
		tab_List.tab_Jungsan.Enable = false;
    }
    else if (strCode == "SEARCH")	///조회
    {
		fn_search();
    }
    else if (strCode == "SAVE")	///저장
    {

		if (cob_Media2.Value = "0" && cob_Media3.Value = "0")
		{
			ds_TLEV_USEAPPRV.SetColumn(rowCnt, "MDM_CD", "");
		}
		else
		{
			if (cob_Media2.Value != "0")
			{
				ds_TLEV_USEAPPRV.SetColumn(rowCnt, "MDM_CD", cob_Media2.Value);
			}
			if (cob_Media3.Value != "0")
			{
				ds_TLEV_USEAPPRV.SetColumn(rowCnt, "MDM_CD", cob_Media3.Value);
			}
		}


		if (Confirm("저장하시겠습니까?"))
		{
			fn_Save();
		}
	}
	else if (strCode == "EXCEL")	///엑셀저장
	{

    }
    else if (strCode == "DELETE")	///삭제
    {
		if (Confirm("삭제하시겠습니까?"))
		{
			fn_Del();
		}
    }
    else if (strCode == "PRINT")	///출력
    {

    }
    else if (strCode == "CLOSE")	///닫기
    {
		close();
    }
}

/*
 * 기      능: 매체코드조회
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
 */
function cob_Media_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var objId = obj.ID;

	if (objId = "cob_Media1")
	{
		gfn_PubMdmCode("ds_Mdmcd2", 2, 2, strCode);
		gfn_PubMdmCode("ds_Mdmcd3", 2, 3, strCode);
	}
	else if (objId = "cob_Media2")
	{
		gfn_PubMdmCode("ds_Mdmcd3", 2, 3, strCode);
	}

}

