/*
 * 기      능: 상세정보조회
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
 */
function fn_Search()
{
	ds_TLEV_USEAPPRV.SetColumn(rowCnt, "APPRV_NUM", gv_Form_Args);

	gfn_SyncCall("svcSearch","KOMCA?SYSID=PATHFINDER&MENUID=1000001009002001&EVENTID=apprv_detail","S=ds_TLEV_USEAPPRV",
			"ds_TLEV_USEAPPRV=SEL1 ds_TLEV_APPTNPRESINFO=SEL2 ds_TLEV_USEQTY=SEL3 "
			+ "ds_TLEV_USEWRTG=SEL4 ds_TLEV_CLRREC=SEL5 ds_TLEV_USEDEMDREPT=SEL6", "", "fn_CallBack");

}

/*
 * 기      능: 저장
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
 */
function fn_Save()
{
	ds_TLEV_USEAPPRV.SetColumn(rowCnt, "INSPRES_ID", gv_USER_ID);
	ds_TLEV_USEAPPRV.SetColumn(rowCnt, "MODPRES_ID", gv_USER_ID);

	gfn_SyncCall("svcSave","KOMCA?SYSID=PATHFINDER&MENUID=1000001009002001&EVENTID=apprv_save","S=ds_TLEV_USEAPPRV:U "
			+"A=ds_TLEV_USEQTY:U B=ds_TLEV_APPTNPRESINFO:U C=ds_TLEV_USEWRTG:U D=ds_TLEV_USEDEMDREPT:U",
			"", "", "fn_CallBack");

}

/*
 * 기      능: 삭제
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
 */
function fn_Del()
{
	ds_TLEV_USEAPPRV.SetColumn(rowCnt, "APPRV_NUM", gv_Form_Args);

	gfn_SyncCall("svcDel","KOMCA?SYSID=PATHFINDER&MENUID=1000001009002001&EVENTID=apprv_delete","S=ds_TLEV_USEAPPRV",
			"", "", "fn_CallBack");

}

/*
 * 기      능: Callback함수
 * 인      수: Argument	설명
 * Return    : Return Value	
 * 예     시 : 함수 사용 예	
 */
function fn_CallBack(svcid,errCode,errMsg) {

	if (errCode = 0)
	{
		switch (svcid)
		{
			case "svcSearch" :
				break;

			case "svcSave" :
				gfn_SetStatusMsg("저장이 완료되었습니다.");
				break;

			case "svcDel" :
				gfn_SetStatusMsg("삭제가 완료되었습니다.");
				break;

			default : break;			
		}		
	}

}

function fn_search() {
	gfn_SyncCall("svcSearch","KOMCA?SYSID=PATHFINDER&MENUID=1000001006003&EVENTID=apprv_search","S=ds_TLEV_USEAPPRV",
			"ds_TLEV_USEAPPRV=SEL1 ds_TLEV_APPTNPRESINFO=SEL2 ds_TLEV_USEWRTG=SEL3 ds_TLEV_USEDEMDREPT=SEL4 ", "", "fn_CallBack");

}
