var btn_print;	//프린트 속성을 기억
function bra01_s11_OnLoadCompleted(obj)
{
    this.ds_in.AddRow();
	gfn_LoadForm(obj);
	gfn_GetDataGIBU_CD(this.Div0.cob_BRAN_CD, 1);
	//Div0.cal_Yrmn.Value = today();
	// 초기설정
	var curYMD = today();
	//div0.cal_SDay.Value = substr(curYMD, 0, 6) + "01" ;  // 현재월의 첫날 부터
	div0.cal_SDay.Value = curYMD;  
	div0.cal_EDay.Value = curYMD;	// 현재일자 까지
	div0.cal_OPBI_DAY.Value = today();	// 납부일자-_-
	
	btn_print = 0;
	this.Div0.cob_gbn.Value = 'M';
	gfn_SetPermission(this.Div0.cob_BRAN_CD.Value);	
}


//	공통버튼 컨트롤
function fn_CommmonButtonClick(strCode)
{
    //신규
    if(strCode == "NEW"){
		fn_New();
    }
    //조회
    else if(strCode == "SEARCH"){
		fn_Search();
    }
    //저장
    else if(strCode == "SAVE"){
		fn_Save();
    }    
    //삭제
    else if(strCode == "DELETE"){
		fn_Delete();
    }
    //Excel
    else if(strCode == "EXCEL"){
		fn_Excel();
    }
    //출력
    else if(strCode == "PRINT"){
		fn_Print();
    }
    //닫기
    else if(strCode == "CLOSE"){
        close();
    }

	return;
}

//조회
function fn_Search(){
	// 입력 필수 항목 검사;
	if (!gfn_CheckFormNull(div0)) return;
	this.ds_In.SetColumn(0, "CRET_GBN", this.Div0.cob_gbn.Value);
	this.ds_In.SetColumn(0, "BRAN_CD", this.Div0.cob_BRAN_CD.Value);
	//this.ds_In.SetColumn(0, "DEMD_YRMN", substr(this.Div0.cal_Yrmn.Value,0,6) );
	this.ds_In.SetColumn(0, "DEMD_FROM", this.Div0.cal_SDay.Value);
	this.ds_In.SetColumn(0, "DEMD_TO", this.Div0.cal_EDay.Value);
	
	
	//trace(this.ds_in.SaveXML());
	gfn_syncCall("sys_bra03_s10_S","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005009&EVENTID=indtnpaper_list","S=ds_in","ds_ubi=SEL1","","fn_CallBack");
	ds_grd_search.Copy("ds_ubi");
	//trace(this.ds_ubi.SaveXML());
}

