function form_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	gfn_GetDataGIBU_CD(this.cob_BRAN_CD, 1);

	gfn_SetPermission(this.cob_BRAN_CD.Value);

	//은행코드 데이터셋 획득
	ds_code_bank.ClearData();
	gfn_syncCall("SEARCH_BANK_CODE","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=search_bank_code","","ds_code_bank=SEL1","","fn_CallBack");


	//this.cob_BRAN_CD.value = "O";
	//this.edt_UPSO_CD.Text = "0110115O";
}


function fn_CommmonButtonClick(strCode){

    //신규
    if(strCode == "NEW"){
		fn_New();
    }
    //조회
    else if(strCode == "SEARCH"){
		fn_Search();
    }
    //저장
    else if(strCode == "SAVE"){
		fn_Save();
    }
    //삭제
    else if(strCode == "DELETE"){
		fn_Delete();
    }
    //Excel
    else if(strCode == "EXCEL"){}
    //출력
    else if(strCode == "PRINT"){}
    //닫기
    else if(strCode == "CLOSE"){
        close();
    }

	return;
}

function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{

	if(ErrorCode < 0) {
		//에러 공통 팝업
		gfn_SetErrorPop(ErrorCode,ErrorMsg);
		return;
	}
	switch(srvId)
	{
		case "SEARCH_DETAIL" :
			if(length(ds_application_form.GetColumn(0, "FILE_TEMPNM")) > 0)
			{
				img_down.Visible = true;
			}
			else
			{
				img_down.Visible = false;
			}
			ds_application_form.SetColumn(0, "PHON_NUM", trim(replace(ds_application_form.GetColumn(0, "PHON_NUM"), "-", "")));
			break;
	}
}

function fn_Search() 
{
	// 입력 필수 항목 검사;
	if (this.cob_BRAN_CD.value == null) {
		alert("센터명을 선택하여 주시오.");
		return;
	}
	if (this.edt_UPSO_CD.Text == null) {
		alert("업소를 지정하여 주십시오.");
		return;
	}

	this.ds_upso.ClearData();
	this.ds_account.ClearData();
	this.ds_application_list.ClearData();
	this.ds_application_form.ClearData();
	this.ds_upso_condi.SetColumn(0,"UPSO_CD", this.edt_UPSO_CD.Text);

	//trace(this.ds_upso_condi.SaveXML());
	gfn_syncCall("UPSO_DETAIL","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=search_info","S=ds_upso_condi","ds_upso=SEL1 ds_account=SEL2 ds_application_list=SEL3","","fn_CallBack");

	if ( this.ds_account.rowcount == 1 && this.ds_account.GetColumn(0, "TERM_YN") == "N") {
		if (this.ds_account.GetColumn(0, "RECEPTION_GBN") == "4" || 
		    this.ds_account.GetColumn(0, "RECEPTION_GBN") == "5" ||
		    this.ds_account.GetColumn(0, "RECEPTION_GBN") == "6") { //계좌 자동이체
			stat_info_acc.Text = "계좌정보";
			edt_bank_nm.value      = this.ds_account.GetColumn(0, "BANK_NM");
			edt_auto_accnnum.value = this.ds_account.GetColumn(0, "AUTO_ACCNNUM");
		} else {													//카드 자동이체 7,8
			stat_info_acc.Text = "카드정보";
			edt_bank_nm.value      = this.ds_account.GetColumn(0, "CARD_GBN");
			edt_auto_accnnum.value = this.ds_account.GetColumn(0, "CARD_NUM");
		}
		edt_paypres_nm.value   = this.ds_account.GetColumn(0, "PAYPRES_NM");
		edt_resinum.value      = this.ds_account.GetColumn(0, "RESINUM");
	} else {
		stat_info_acc.Text = "계좌정보";
		edt_bank_nm.value      = "";
		edt_auto_accnnum.value = "";
		edt_paypres_nm.value   = "";
		edt_resinum.value      = "";
	}
	this.ubi_list.visible = true;
}

function fn_Search2() 
{
	// 입력 필수 항목 검사;
	if (this.cob_BRAN_CD.value == null) {
		alert("센터명을 선택하여 주시오.");
		return;
	}
	if (this.edt_UPSO_CD.Text == null) {
		alert("업소를 지정하여 주십시오.");
		return;
	}
	
	this.ds_upso.ClearData();
	this.ds_account.ClearData();
	this.ds_application_list.ClearData();
	this.ds_application_form.ClearData();
	this.ds_upso_condi.SetColumn(0,"UPSO_CD", this.edt_UPSO_CD.Text);
	
	//trace(this.ds_upso_condi.SaveXML());
	gfn_syncCall("UPSO_DETAIL","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=search_info","S=ds_upso_condi","ds_upso=SEL1 ds_account=SEL2 ds_application_list=SEL3","","fn_CallBack");

	if ( this.ds_account.rowcount == 1 && this.ds_account.GetColumn(0, "TERM_YN") == "N") {
		if (this.ds_account.GetColumn(0, "RECEPTION_GBN") == "4" || 
		    this.ds_account.GetColumn(0, "RECEPTION_GBN") == "5" ||
		    this.ds_account.GetColumn(0, "RECEPTION_GBN") == "6") { //계좌 자동이체
			stat_info_acc.Text = "계좌정보";
			edt_bank_nm.value      = this.ds_account.GetColumn(0, "BANK_NM");
			edt_auto_accnnum.value = this.ds_account.GetColumn(0, "AUTO_ACCNNUM");
		} else {													//카드 자동이체 7,8
			stat_info_acc.Text = "카드정보";
			edt_bank_nm.value      = this.ds_account.GetColumn(0, "CARD_GBN");
			edt_auto_accnnum.value = this.ds_account.GetColumn(0, "CARD_NUM");
		}
		edt_paypres_nm.value   = this.ds_account.GetColumn(0, "PAYPRES_NM");
		edt_resinum.value      = this.ds_account.GetColumn(0, "RESINUM");
	} else {
		stat_info_acc.Text = "계좌정보";
		edt_bank_nm.value      = "";
		edt_auto_accnnum.value = "";
		edt_paypres_nm.value   = "";
		edt_resinum.value      = "";
	}
	this.ubi_list.visible = true;
	
	gfn_setGridFocus(grd_bank, ds_application_list.rowcount-1, "INS_DATE");	
	
	this.ds_application_search.ClearData();
	this.ds_application_search.AddRow();
	
	this.ds_application_search.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.Value);
	this.ds_application_search.SetColumn(0,"UPSO_CD", this.ds_application_list.GetColumn(ds_application_list.rowcount-1, "UPSO_CD"));
	this.ds_application_search.SetColumn(0,"SEQ", this.ds_application_list.GetColumn(ds_application_list.rowcount-1, "SEQ"));
	
	gfn_syncCall("SEARCH_DETAIL","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=search_detail","S=ds_application_search","ds_application_form=SEL1 ds_code_staff=SEL2","","fn_CallBack");
	
	
	ds_code_applic_gbn.ClearData();
	ds_code_applic_gbn.Copy("ds_code_applic_gbn1");
	
	//조회 데이터에 따른 수정가능 조정, 보여지는 부분 수정
	// 접수처구분(4:계좌은행, 5:계좌협회, 6:계좌금융결제원, 7:카드협회, 8:카드카드)
	var rectption_gbn = ds_application_form.GetColumn(0, "RECEPTION_GBN");
	
	if (rectption_gbn == "4" || rectption_gbn == "5" || rectption_gbn == "6") {	//계좌 자동이체
		set_application_form_visible("ACCN");
	}
	else if (rectption_gbn == "7" || rectption_gbn == "8") {	//카드 자동이체
		set_application_form_visible("CARD");
	}
	
	var confirm_nm_len = length(trim(ds_application_form.GetColumn(0, "CONFIRM_NM")));
	if (rectption_gbn == "4" || rectption_gbn == "6" || rectption_gbn == "8") {	//1. 접수처 구분이 은행, 금융결제원, 카드사이면 수정불가
		set_application_form_enable(false);
	} else if ((rectption_gbn == "5" || rectption_gbn == "7") && confirm_nm_len > 0 ) {	//2. 접수처 구분이 협회일때 확인자 값이 있으면 수정불가
		set_application_form_enable(false);			//   확인 후 수정을 하고싶으면 센터업무팀 자동이체담당자가 81파일 생성 전에 확인자가 확인해지해야함.
	} else if ((rectption_gbn == "5" || rectption_gbn == "7") && confirm_nm_len == 0) {	//3. 접수처 구분이 협회이고 확인자 값이 없으면 수정할 수 있음
		set_application_form_enable(true);
	}
}


function fn_New() 
{
	fn_Search();

	//직원코드 데이터셋 획득
	this.ds_upso_condi.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.value);
	gfn_syncCall("SAWON_CLICK", "KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select", "S=ds_upso_condi","ds_code_staff=SEL1","","fn_CallBack");

	//신청구분 값 변경
	ds_code_applic_gbn.ClearData();
	ds_code_applic_gbn.Copy("ds_code_applic_gbn2");

	this.ds_application_form.ClearData();
	this.ds_application_form.AddRow();

	//업소코드 지정
	this.ds_application_form.SetColumn(0, "UPSO_CD", this.edt_UPSO_CD.Text);

	set_application_form_enable(true);
}





function fn_Save() 
{
	if (this.element_1.Enable == false) {
		gfn_Confirm("MB_OK", "수정할 수 있는 데이터가 아닙니다.", "경고", "0");
		return;
	}
	
	
	//신청 중복 안되도록
	var cnt = 0;
	
	for(var i = 0; i < ds_application_list.rowcount; i++)
	{
		if( ds_application_list.GetColumn(i, "REMAK") == "" ){	cnt++;	}
	}
	if(cnt >0)
	{
		alert("이전 신청정보가 있습니다. 확인 후 다시 저장하세요");
		return;
	}

    if (!gfn_CheckFormNull(this)) return;
    
    //신청구분이 카드인경우에 대한 form

	//주민번호 정합성 체크
	var resinum1 = this.ds_application_form.GetColumn(0, "RESINUM_1");
	var resinum2 = this.ds_application_form.GetColumn(0, "RESINUM_2");
	
	// 13 자리가 모두 입력된 경우만 주민번호 검사. 뒷자리가 000으로 채워진 경우는 주민번호검사하지 않아요.
	// 사업자번호인경우를 생각해서 00000에서 000으로 변경
	if (length(resinum1) == 6 && length(resinum2) == 7 && substr(resinum2,4)	!= "000") {
		if (gfn_CheckJumin(resinum1, resinum2) < 0) {
			gfn_Confirm("MB_OK", "자동이체 사용불가 (주민번호 오류)", "경고", "0");
			this.element_7.SetFocus();
			return;
		}
	} else if (length(resinum1) == 6 && (length(resinum2) == 1 || length(resinum2) == 2)) {
		alert("주민번호를 일부만 알고있는 경우는 뒷부분을 0으로 채워 13자리를 맞춰주세요");
		this.element_7.SetFocus();
		return;
	}
			
	/*if (length(resinum1) + length(resinum2) != 10 && gfn_CheckJumin( resinum1, resinum2) < 0) {
		gfn_Confirm("MB_OK", "자동이체 사용불가 (주민번호 오류)", "경고", "0");	
		this.element_7.SetFocus();
		return;
	}*/
	
	//자동이체동의자료 금결원 전송 시행 후 신규의 경우 자동의체동의자료 없이 저장을 막음
	if(ds_application_form.GetColumn(0, "APPLICATION_GBN") == "02" && (ds_auto_ins_file.rowcount != 1 || length(ds_application_form.GetColumn(0, "FILE_TEMPNM")) < 1))
	{
		alert("자동이체동의자료 파일을 첨부해주세요.");
		this.btnFileSch.SetFocus();
		return;
	}

	this.ds_application_form.SetColumn(0, "RESINUM", ""+resinum1 + resinum2);
	this.ds_application_form.SetColumn(0, "INSPRES_ID", gv_USER_ID);

	if(!confirm("저장하시겠습니까?"))
	{
	    return;
	}

	gfn_syncCall("SAVE","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=save","S=ds_application_form:U","ds_get_seq1=SEL7 ds_get_seq2=SEL8","","fn_CallBack",gv_TraceLog,GV_FORMID);


	//if (ds_auto_ins_file.rowcount == 1) {
		var str_seq_ds = "";
		if (ds_get_seq1.rowcount == 1) 
			str_seq_ds = "ds_get_seq1";
		else
			str_seq_ds = "ds_get_seq2";

		//alert(ds_get_seq1.GetColumn(0, "UPSO_CD") + ", " + ds_get_seq2.GetColumn(0, "UPSO_CD"));
		gfn_SyncCall("upso_doc_fileup","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=applicaiton_fileup","S=ds_auto_ins_file S1="+str_seq_ds,"","","fn_CallBack",gv_TraceLog,GV_FORMID);
	//}

	fn_Search2();
}


function fn_Delete()
{
	if (this.element_1.Enable == false) {
		gfn_Confirm("MB_OK", "삭제할 수 있는 데이터가 아닙니다.", "경고", "0");
		return;
	}

	// 데이터에 따른 삭제가능 판단
	// 접수처구분(4:계좌은행, 5:계좌협회, 6:계좌금융결제원, 7:카드협회, 8:카드카드)
	var rectption_gbn = ds_application_form.GetColumn(0, "RECEPTION_GBN");
	var confirm_nm_len = length(trim(ds_application_form.GetColumn(0, "CONFIRM_NM")));
	//1. 접수처 구분이 은행, 금융결제원, 카드사이면 수정불가
	if (rectption_gbn == "4" || rectption_gbn == "6" || rectption_gbn == "8") {
		gfn_Confirm("MB_OK", "협회접수분만 삭제할 수 있습니다.", "경고", "0");
		return;
	}
	//2. 접수처 구분이 협회일때 확인자 값이 있으면 수정불가
	//   확인 후 수정을 하고싶으면 센터업무팀 자동이체담당자가 81파일 생성 전에 확인자가 확인해지해야함.
	else if ((rectption_gbn == "5" || rectption_gbn == "7") && confirm_nm_len > 0 ) {
		gfn_Confirm("MB_OK", "삭제할 수 있는 데이터가 아닙니다.", "경고", "0");
		return;
	}
	//3. 접수처 구분이 협회이고 확인자 값이 없으면 수정할 수 있음
	else if ((rectption_gbn == "5" || rectption_gbn == "7") && confirm_nm_len == 0) {
		if(!confirm("삭제하시겠습니까?")){
			return;
		}
		this.ds_application_form.DeleteAll();
		gfn_syncCall("SAVE","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005011&EVENTID=save","S=ds_application_form:U","ds_get_seq1=SEL7 ds_get_seq2=SEL8","","fn_CallBack",gv_TraceLog,GV_FORMID);

		fn_Search();
	}
}

function btn_FindUpso_OnClick(obj)
{

	if (length(this.cob_BRAN_CD.Value) == 0) {
        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return;
	}

	fn_FindUPSO();

}

function fn_FindUPSO()
{
	//업소코드 입력 조회시 바로 조회창 페이지를 거치지 않고 바로 검색
	if(this.cob_BRAN_CD.Value==null){

        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}

	this.edt_UPSO_NM.SetFocus();
	var BRAN_CD = this.cob_BRAN_CD.Value;
	var UPSO_CD = this.edt_UPSO_CD.Text;
	var UPSO_NM = this.edt_UPSO_NM.Text;
    var inParam = "SGBN=bra05_s01" + " BRAN_CD=" + quote(BRAN_CD) + " UPSO_CD=" + quote(UPSO_CD) + " UPSO_NM=" + quote(UPSO_NM) + " SGBN=" + quote("bra01_s01");
	var result = Dialog("fi_bra::find_upso.xml",inParam);

	if(0 < length(result)){
	    var vArr = result.split(",");
	    this.edt_UPSO_CD.Text = vArr[0];
	    this.edt_UPSO_CD_1.Text = vArr[0];    
	    this.edt_UPSO_NM.Text = vArr[1];
	    this.edt_UPSO_NM_1.Text = vArr[1];    

	}
	fn_search();
}


function edt_UPSO_CD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(this.cob_BRAN_CD.Value==null){

        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}

	if (nChar == "13") {
		fn_GetUPSO_CD_cd();

	}
	else if (nChar == "112") fn_FindUPSO();

}

function fn_GetUPSO_CD()
{

	var BRAN_CD = this.cob_BRAN_CD.Value;
	var UPSO_CD = this.edt_UPSO_CD.Text;
	var UPSO_NM = this.edt_UPSO_NM.Text;

	var result = gfn_GetUPSO_CD(BRAN_CD, UPSO_CD, UPSO_NM);

	if (length(result) > 0) {
		var vArr = result.split(",");
		this.edt_UPSO_CD.Text = vArr[0];
		this.edt_UPSO_NM.Text = vArr[1];
		fn_search();
	}
	else {

		fn_FindUPSO();

	}
	this.edt_UPSO_NM.Text = this.edt_UPSO_NM_1.Text ;
}


function edt_UPSO_NM_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(cob_BRAN_CD.Text.Length() ==0 ) 
	{
        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		cob_BRAN_CD.SetFocus();
		return;
	}
	if (nChar == "112") {
		fn_FindUPSO();
	}

	return;
}

function fn_GetUPSO_CD_cd()
{
	if(this.edt_UPSO_NM.Text == ""){
		var BRAN_CD = this.cob_BRAN_CD.Value;
		var UPSO_CD = this.edt_UPSO_CD.Text;
		var UPSO_NM = this.edt_UPSO_NM.Text;
		var result = gfn_GetUPSO_CD(BRAN_CD, UPSO_CD, UPSO_NM);

		if (length(result) > 0) {
			var vArr = result.split(",");
			this.edt_UPSO_CD.Text = vArr[0];
			this.edt_UPSO_NM.Text = vArr[1];

			fn_search();
		}
		else {

			fn_FindUPSO();
		}
	}
	else {

		var BRAN_CD = this.cob_BRAN_CD.Value;
		var UPSO_CD = this.edt_UPSO_CD.Text;
		var UPSO_NM = "";
		var result = gfn_GetUPSO_CD(BRAN_CD, UPSO_CD, UPSO_NM);

		if (length(result) > 0) {
			var vArr = result.split(",");
			this.edt_UPSO_CD.Text = vArr[0];
			this.edt_UPSO_NM.Text = vArr[1];
			fn_search();
		}
		else {

			fn_FindUPSO();
		}

	}
}
function edt_UPSO_CD_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	var strtext = right(strPostText,1);
	var chkstr = "[<!#$%*&_:=|`~'"+Quote("")+";@^+\\>-]"; 

	if(pos(chkstr, strtext) >= 0)   // 특수문자 사용 제한
	{
		return false;
	}

	if (length(strPostText) == 8 && nChar >= asc('a') && nChar <= asc('z')) {
		obj.Text = strPreText + toUpper(chr(nChar));
		return false;
	}
}

