function element_1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (this.element_1.Enable == false) return;

	if (element_1.Value == "02" || element_1.Value == "12") {				//계좌신규, 카드신규
		if (length(ds_upso.GetColumn(0, "CLSBS_YRMN")) == 8 ) {
			gfn_Confirm("MB_OK", "자동이체 신청불가 (폐업업소)", "경고", "0");	element_1.Value = "";	return;
		} else if (ds_account.GetColumn(0, "TERM_YN") == "N") {
			gfn_Confirm("MB_OK", "자동이체 신청불가 (자동이체 관리업소)", "경고", "0");	element_1.Value = "";	return;
		} 
	} else if (element_1.Value == "08") {							//계좌해지
		if (ds_account.rowcount == 0 || ds_account.GetColumn(0, "TERM_YN") == "Y" ||
		    ds_account.GetColumn(0, "RECEPTION_GBN") == "7") {
			gfn_Confirm("MB_OK", "자동이체 해지불가", "경고", "0");	element_1.Value = "";	return;
		}
	} else if (element_1.Value == "18") {							//카드해지
		if (ds_account.rowcount == 0 || ds_account.GetColumn(0, "TERM_YN") == "Y" ||
		    ds_account.GetColumn(0, "RECEPTION_GBN") == "4" || ds_account.GetColumn(0, "RECEPTION_GBN") == "5" || ds_account.GetColumn(0, "RECEPTION_GBN") == "6") {
			gfn_Confirm("MB_OK", "카드자동 해지불가", "경고", "0");	element_1.Value = "";	return;
		}
	}


	if (element_1.Value == "02" || element_1.Value == "08") {			//계좌 신규, 해지일때
		set_application_form_visible("ACCN");
		this.ds_application_form.SetColumn(0, "RECEPTION_GBN", "5");
	} else if (element_1.Value == "12" || element_1.Value == "18") {	//칻 신규, 해지일때
		set_application_form_visible("CARD");
		this.ds_application_form.SetColumn(0, "RECEPTION_GBN", "7");
	} 
}



