function element_9_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	// 조정환과장 요청 2020-02-07 경영주 허가주 정보 자동삽입
	if(element_9.Value == "1") // 경영주
	{
		element_6.Text = ds_upso.GetColumn(0, "MNGEMSTR_NM"); // 납부자명
		element_10.Text = replace(ds_upso.GetColumn(0, "MNGEMSTR_HPNUM"), "-", ""); // 전화번호
		element_7.Text = substr(ds_upso.GetColumn(0, "MNGEMSTR_RESINUM"), 0, 6); // 주민번호앞
		if(length(substr(ds_upso.GetColumn(0, "MNGEMSTR_RESINUM"), 0, 6)) == 6) // 주민번호 앞자리가 존재할 때에만
		{
			element_8.Text = "0000000"; // 주민번호뒤
		}
	}
	else if(element_9.Value == "2") // 허가주
	{
		element_6.Text = ds_upso.GetColumn(0, "PERMMSTR_NM"); // 납부자명
		element_10.Text = replace(ds_upso.GetColumn(0, "PERMMSTR_HPNUM"), "-", ""); // 전화번호
		element_7.Text = substr(ds_upso.GetColumn(0, "PERMMSTR_RESINUM"), 0, 6); // 주민번호앞
		if(length(substr(ds_upso.GetColumn(0, "PERMMSTR_RESINUM"), 0, 6)) == 6) // 주민번호 앞자리가 존재할 때에만
		{
			element_8.Text = "0000000"; // 주민번호뒤
		}
	}
	else
	{
		element_6.Text = ""; // 납부자명
		element_10.Text = ""; // 전화번호
		element_7.Text = ""; // 주민번호앞
		element_8.Text = ""; // 주민번호뒤
	}
}

