function tr_result(serid,errid,errmsg){	if(serid == "fileup")	{
		var str_nm = dsUploadFile.getcolumn(0,"UPFILENAME");
		var str_ext =substr(str_nm, length(str_nm)-3);

		if (str_ext == "jpg" || str_ext == "JPG" || str_ext == "bmp" || str_ext == "BMP" || str_ext == "gif" || str_ext == "GIF" || str_ext == "pdf" || str_ext == "PDF" ) {			this.edt_img_file.Text = dsUploadFile.getcolumn(0,"UPFILENAME");
		} else if (length(str_nm) != 0){
			alert("확장자가 jpg, bmp, gif, pdf인 이미지만 업로드할 수 있습니다.");
			dsUploadFile.ClearData();
		}	}}


function show_report()
{

	var path="";
	if (gv_svr_ip == "172.17.10.100") {
		path = "http://112.175.137.148/upload/";
	} else {
		path = "http://112.175.137.147/upload/";
	}

    this.ubi_list.setPageScale("140");
	this.ubi_list.setJrfFileDir(gv_ReportUrl+"fi/bra/");
	this.ubi_list.setJrfFileName("show_image.jrf"); 
	this.ubi_list.setArgument("ar_img_url", path + urlEncodeUtf8(ds_application_form.GetColumn(0, "FILE_TEMPNM")));

	this.ubi_list.visible = true;
	this.ubi_list.Retrieve();
}

function set_application_form_enable( value )
{
	for (var i=1; i<=16; i++) {
		if (i==3) { continue; }	// 등록일자 항목은 수정하지 않는다.
		eval("element_" + i).Enable = value ;
	}

	if ( value == true ) element_8.Password = false;
	else element_8.Password = true;

	this.ubi_list.Visible = false;
	show_report();
}


function set_application_form_visible( value )
{
	if (value == "ACCN") {
		stat_bank_nm.Text = "은행명"; 		stat_accn_num.Text = "계좌번호";	Static12.Text = "업주와의 관계";
		stat_expire_day.Visible = false;	element_4.Visible = true;			element_5.Visible = true;		
		element_13.Visible = false;			element_14.Visible = false;			element_15.Visible = false;
		element_9.Visible = true;			element_16.Visible = false;
		element_4.BKColor = "user13";		element_5.BKColor = "user13";		element_13.BKColor = "-1";		
		element_14.BKColor = "-1";			element_15.BKColor = "-1";
		element_13.Value = "";				element_14.Value = "";				element_15.Value = "";

	} else if (value == "CARD") {
		stat_bank_nm.Text = "카드사"; 		stat_accn_num.Text = "카드번호";	Static12.Text = "사업자번호";
		stat_expire_day.Visible = true;		element_4.Visible = false;			element_5.Visible = false;		
		element_13.Visible = true;			element_14.Visible = true;			element_15.Visible = true;
		element_9.Visible = false;			element_16.Visible = true;
		element_4.BKColor = "-1";			element_5.BKColor = "-1";			element_13.BKColor = "-1";		
		element_14.BKColor = "-1";			element_15.BKColor = "-1";
		element_4.Value = "";	 			element_5.Value = "";

	}
}
