function btn_goso_OnClick(obj)
{
	// 고소정보가 여러개인 경우의 처리
	if (toInteger(this.ds_upso.GetColumn(0, "ACCU_CNT")) > 1) {
		var inParam = "BRAN_CD=" + quote(this.cob_BRAN_CD.Value) 
		            + " UPSO_NM=" + quote(this.ds_upso.GetColumn(0, "UPSO_NM"))
					+ " MNGEMSTR_NM=" + quote(this.ds_upso.GetColumn(0, "MNGEMSTR_NM"))
					+ " SGBN=" + quote("bra04_r04");
				
		var result = Dialog("fi_bra::find_accu_list.xml",inParam);

		if(length(result) > 0){
			var vArr = result.split(",");
		
			g_bran_cd   = this.cob_BRAN_CD.Value;
			g_upso_cd   = this.ds_upso.GetColumn(0, "UPSO_CD"  );
			g_upso_nm   = this.ds_upso.GetColumn(0, "UPSO_NM"  );
			g_accu_day	= vArr[0];
			g_accu_num	= vArr[1];
			g_accu_bran = vArr[2];
			g_accu_gbn	= vArr[3];

			gfn_OpenChildWindow("fi_bra::bra05_s03.xml", "고소내역등록","");
		}
	}
	// 고소정보가 하나인 경우의 처리
	else {
		g_bran_cd   = this.cob_BRAN_CD.Value;
		g_upso_cd   = this.ds_upso.GetColumn(0, "UPSO_CD"  );
		g_upso_nm   = this.ds_upso.GetColumn(0, "UPSO_NM"  );
		g_accu_day	= this.ds_upso.GetColumn(0, "ACCU_DAY" );
		g_accu_num	= this.ds_upso.GetColumn(0, "ACCU_NUM" );
		g_accu_bran = this.ds_upso.GetColumn(0, "ACCU_BRAN");
		g_accu_gbn	= this.ds_upso.GetColumn(0, "ACCU_GBN" );

		gfn_OpenChildWindow("fi_bra::bra05_s03.xml", "고소내역등록","");
	}
}

