		this.edt_UPSO_CD.Text = g_upso_cd;

		if (length(g_upso_year) > 0) {
			this.spn_YYYY.value = g_upso_year;
		}
		
		g_bran_cd	=	"";
		g_upso_cd	=	"";
		g_upso_year	=	"";
		
		fn_search();
    }
}


