function grd_wonjang_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var month;
	var temp_mon = toNumber(nRow)+1;
	var temp_month = length(temp_mon+"");
	if(nCell==1 || nCell==2 || nCell==3 || nCell==4){
		if(temp_month==1){
			month = this.edit6.Text+'0'+temp_mon;
		}
		else if(temp_month==2){
			month = this.edit6.Text+temp_mon;
		}
	}
	else if(nCell==5 || nCell==6 || nCell==7 || nCell==8){
		if(temp_month==1){
			month = this.edit5.Text+'0'+temp_mon;
		}
		else if(temp_month==2){
			month = this.edit5.Text+temp_mon;
		}
	}
	else if(nCell==9 || nCell==10 || nCell==11 || nCell==12){
		if(temp_month==1){
			month = this.edit3.Text+'0'+temp_mon;
		}
		else if(temp_month==2){
			month = this.edit3.Text+temp_mon;
		}
	}

	this.ds_in2.SetColumn(0,"NOTE_YRMN", month);
	this.ds_in2.SetColumn(0,"UPSO_CD", this.edt_UPSO_CD.Text );
//	trace(this.ds_in2.SaveXML());
	gfn_syncCall("sys_balance_detail","KOMCA?SYSID=PATHFINDER&MENUID=1000001006001008001&EVENTID=upso_rept_detail","S=ds_in2","ds_rept_detail=SEL1","","fn_CallBack");

	if(this.ds_rept_detail.RowCount()==0){
		this.ds_rept_detail.AddRow();
	}
//	trace(this.ds_rept_detail.SaveXML());
		
}

function btn_send_sms_OnClick(obj,nX,nY){
	gds_sms_send.cleardata();	gds_sms_send.AddRow();

	//요금조견표 산식으로 요금계산시작
	// 변수 : 업소코드, 시작월, 끝월, 자동이체여부
	ds_demd_amt_in.AddRow();

	ds_demd_amt_in.SetColumn(0,"UPSO_CD", ds_upso.GetColumn(0,"UPSO_CD"));

	if (length(this.ds_history.GetColumn(0, "REPT_YRMN")) == 0) {		ds_demd_amt_in.SetColumn(0,"START_YRMN", this.ds_history.GetColumn(0, "OPBI_DAY"));	} else {		ds_demd_amt_in.SetColumn(0,"START_YRMN", addMonth(this.ds_history.GetColumn(0, "REPT_YRMN") + "01", 1));	}		
	ds_demd_amt_in.SetColumn(0,"END_YRMN", today());
	//if (this.edit9.Text == "자동이체") {
	//	ds_demd_amt_in.SetColumn(0,"DEMD_GBN", "A");	
	//} else {
		ds_demd_amt_in.SetColumn(0,"DEMD_GBN", "O");	//20121122 김현이 계장 요청.
	//}
	gfn_syncCall("get_demd_amt","KOMCA?SYSID=PATHFINDER&MENUID=1000001006001008001&EVENTID=get_demd_amt","S=ds_demd_amt_in","ds_demd_amt_out=SEL2","","fn_CallBack");
	//요금조견표 산식으로 요금계산끝


	if(length(this.ds_upso.GetColumn(0,"MNGEMSTR_HPNUM"))==0){
		this.btn_send_sms.Enable = false;
	}
	else	this.btn_send_sms.Enable = true;
	gds_sms_send.SetColumn(0,"USER_ID", "U"+this.ds_upso.GetColumn(0,"UPSO_CD") );	gds_sms_send.SetColumn(0,"SCHEDULE_TYPE","0"); //즉시 “0” 예약 “1”	gds_sms_send.SetColumn(0,"SEND_DATE", ""); //즉시는 없음 200912182130	gds_sms_send.SetColumn(0,"SUBJECT", "(사)한국음악저작권협회"); //제목:발송되지는 않음	gds_sms_send.SetColumn(0,"CALLBACK", this.ds_upso.GetColumn(0, "BRAN_PHON_NUM"));//회신번호	gds_sms_send.SetColumn(0,"NAME", this.ds_upso.GetColumn(0, "MNGEMSTR_NM")); //수신자명	gds_sms_send.SetColumn(0,"PHNUM", this.ds_upso.GetColumn(0, "MNGEMSTR_HPNUM")); //수신자핸드폰번호 ‘-‘는 생략
	
	//INFO 메세지 생성

	if (this.cob_BRAN_CD.Value != null) {
		this.ds_search_accn.ClearData();
		this.ds_search_accn.AddRow();
		this.ds_search_accn.SetColumn(0, "BRAN_CD" , this.cob_BRAN_CD.Value);
		this.ds_search_accn.SetColumn(0, "USE_TYPE", gfn_GetAccountUsage("03"));	
		gfn_syncCall("find_account","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=accn_num_select2","S=ds_search_accn","ds_banklist=SEL1","","fn_CallBack");
	}
	ds_sms_info.ClearData();
	ds_sms_info.AddRow();
	ds_sms_info.SetColumn(0, "UPSO_CD",      this.ds_upso.GetColumn(0,"UPSO_CD"));
	ds_sms_info.SetColumn(0, "UPSO_NM",      this.ds_upso.GetColumn(0,"UPSO_NM"));
	ds_sms_info.SetColumn(0, "MNGEMSTR_NM",  this.ds_upso.GetColumn(0,"MNGEMSTR_NM"));
	ds_sms_info.SetColumn(0, "TOT_DEMD_AMT", NumFormat(this.ds_demd_amt_out.GetColumn(0, "TOT_DEMD_AMT")));
	ds_sms_info.SetColumn(0, "ACCN_OWNER",   "(사)한국음악저작권협회");
	ds_sms_info.SetColumn(0, "BRAN_CD", cob_BRAN_CD.Value);
	ds_sms_info.SetColumn(0, "FORM_ID", "bra04_r04");

	if (this.cob_BRAN_CD.Value == "A") {
		ds_sms_info.SetColumn(0, "BANK_NM1",     "국민은행");
		ds_sms_info.SetColumn(0, "ACCN_NUM1",    "695037-01-001228");
	} else {
		ds_sms_info.SetColumn(0, "BANK_NM1",     this.ds_banklist.GetColumn(0, "BANK_NM"));
		ds_sms_info.SetColumn(0, "ACCN_NUM1",    this.ds_banklist.GetColumn(0, "ACCN_NUM"));
	}
	if (this.ds_banklist.RowCount() == 2) {
		ds_sms_info.SetColumn(0, "BANK_NM2", this.ds_banklist.GetColumn(1, "BANK_NM"));
		ds_sms_info.SetColumn(0, "ACCN_NUM2", this.ds_banklist.GetColumn(1, "ACCN_NUM"));
	}
	else {
		ds_sms_info.SetColumn(0, "BANK_NM2", "");
		ds_sms_info.SetColumn(0, "ACCN_NUM2", "");
	}
	Dialog("fi_bra::pop_sms_send.xml", "dsName=ds_sms_info");	//전송 팝업
	//Dialog("ad_pub::env06_r01.xml", "", "", "");	//전송 팝업	
}


function btn_GetDemdRept_OnClick(obj)
{
	if (length(this.edt_UPSO_CD1.Text) > 0) {
		var UPSO_CD = this.edt_UPSO_CD.Text;
		var UPSO_NM = this.edt_UPSO_NM.Text;

		var START_YRMN = "";
		if (length(this.ds_history.GetColumn(0, "REPT_YRMN")) == 0) {
			START_YRMN = this.ds_history.GetColumn(0, "OPBI_DAY");
		}
		else {
			START_YRMN = addMonth(this.ds_history.GetColumn(0, "REPT_YRMN") + "01", 1);
		}
		
		var inParam ="UPSO_CD=" + quote(UPSO_CD) + " UPSO_NM=" + quote(UPSO_NM) + " START_YRMN=" + quote(START_YRMN);

		Dialog("fi_bra::get_DemdAmt.xml",inParam);
	}
}


