function Btn_Send_OnClick(obj)
{
	//로그인하고 성공하면 전화를 건다.
	if (length(Edt_SendNo.Text) != 0) {
		TextArea0.Text = "";
		Fn_MakeCallMessage(Edt_SendNo.text);
		Fn_PBXLogin();
	} else {
		var btn_style = "MB_OK";
		var str_msg = "전화번호가 선택 되어 있지 않습니다.\n확인 하십시오";
		var title = "전화번호 확인";
		var focus = "0";
		var rtn = gfn_Confirm(btn_style, str_msg, title, focus);	
	}

}



