function grd_NONPY_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	var intCol_colid = gfn_GetColumnIndex(grd_NONPY, "COL_CHECK");	if(nCell > 0){		gfn_GridSort(obj,  nCell);	}	if (nCell != intCol_colid) return;	var strCheck = grd_NONPY.GetCellProp("Head",intCol_colid,"value");	if (strCheck == "1")	{		grd_NONPY.SetCellProp("Head", intCol_colid, "value", "0");	}else if (strCheck == "0")	{		grd_NONPY.SetCellProp("Head", intCol_colid, "value", "1");	}	if (strCheck == "1")	{		for(var i=0; i<ds_NONPY.rowcount; i++)		{			if(ds_NONPY.GetColumn(i,"COL_CHECK") == "1"){				ds_NONPY.SetColumn(i, "COL_CHECK", "0");			}			}	}else if (strCheck == "0")	{		for(var i=0; i<ds_NONPY.rowcount; i++)		{			if(ds_NONPY.GetColumn(i,"COL_CHECK") == "0"){				ds_NONPY.SetColumn(i, "COL_CHECK", "1");			}			}	}
}
