}

function fn_CallBack(svcid,errCode,errMsg) 
{
	if (errCode = 0)
	{
		switch(svcid)
		{
			case "svcSearch" :
				break;
			case "svcStaff" :
				ds_SawonInfo.InsertRow(0);
				ds_SawonInfo.SetColumn(0,"STAFF_CD","");
				ds_SawonInfo.SetColumn(0,"STAFF_NM","--ALL--");

				cob_Staff_Nm.Value = gds_sessioninfo.GetColumn(0, "STAFF_NUM");
				if(cob_Staff_Nm.Index < 0)
				{
					cob_Staff_Nm.Index = 0;
				}
				break;
			default: break;
		}
	}
}

function bra05_p01_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);

	bra05_p01_OnSize();

	cal_Sday.Value = substr(today(), 0, 6) + "01";
	cal_Eday.Value = today();

	gfn_syncCall("svcAuth","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008008&EVENTID=sel_udtkpres","","ds_Auth=SEL1","","fn_CallBack");

	if(ds_Auth.FindRow("UDTKPRES_ID", gds_sessioninfo.GetColumn(0, "STAFF_NUM")) > 0 || gds_sessioninfo.GetColumn(0, "BRAN_CD") == "AL" || gds_sessioninfo.GetColumn(0, "DEPT_CD") == "122040100")
	{
		cob_Staff_Nm.Enable = true;
	}
	else
	{
		cob_Staff_Nm.Enable = false;
	}

	// 센터코드 가져오기
	gfn_GetDataGIBU_CD(cob_BRAN_CD, 0);
	gfn_syncCall("find_GIBU","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=c_gibu_select","","ds_GIBU_CD=SEL1","","fn_CallBack");

	if(cob_BRAN_CD.Index < 0)
	{
		cob_BRAN_CD.Index = 0;
	}

	cob_BRAN_CD_OnChanged(this, cob_BRAN_CD.Value, "", "", "");

	if (gv_svr_ip == "192.168.242.119") {
		path = gv_Web2Url + "/upload/";
	} else {
		path = gv_Web1Url + "/upload/";
	}
	/*if(indexOf(ip, "192.168.252.") > -1)
	{
		if (gv_svr_ip == "192.168.242.119") {
			path = "192.168.244.246/upload/";
		} else {
			path = "192.168.241.136/upload/";
		}
	}*/
}

function fn_Search()
{
	if(length(this.cob_BRAN_CD.Value) < 1)
	{
        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}

	gfn_syncCall("svcSearch","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=sel_accu_print","S=ds_search","ds_result=SEL1","","fn_CallBack");
}

function cob_BRAN_CD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	gfn_SetPermission(strCode);

	this.ds_BRAN_CD.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.value);
	gfn_syncCall("svcStaff","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select","S=ds_BRAN_CD","ds_SawonInfo=SEL1","","fn_CallBack");
}

function bra05_p01_OnSize(obj,nCx,nCy,nState)
{
	gfn_ObjreSize(Shape0, this, 1, 0);
	gfn_ObjreSize(grd_result, this, 1, 0);
}

function cal_OnChanged(obj,strOldText,strNewText)
{
	if(obj == cal_Sday)
	{
		if(cal_Sday.Value > cal_Eday.Value)
			cal_Eday.Value = cal_Sday.Value;
	}
	else if(obj == cal_Eday)
	{
		if(cal_Sday.Value > cal_Eday.Value)
			cal_Sday.Value = cal_Eday.Value;
	}
}
function grd_result_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	ubi0.setJrfFileDir(gv_ReportUrl+"fi/bra/");
	ubi0.setServletURL2(gv_ReportUrl2);
	ubi0.FontRevision = true;
	ubi0.IsDeflater = true;
	ubi0.setDatasource("fidu#jdbc/oracle");
	ubi0.SetVariable("exportAsOrgImage", "true");

	ubi0.SetArgument("ACCU_DAY", ds_result.GetColumn(nRow, "ACCU_DAY"));
	ubi0.SetArgument("ACCU_NUM", ds_result.GetColumn(nRow, "ACCU_NUM"));
	ubi0.SetArgument("ACCU_BRAN", ds_result.GetColumn(nRow, "ACCU_BRAN"));
	ubi0.SetArgument("PRINT_DATE", ds_result.GetColumn(nRow, "PRINT_DATE"));
	ubi0.SetArgument("PROD_CD", ds_result.GetColumn(nRow, "PROD_CD"));

	if(nCell == grd_result.GetBindCellIndex("body", "P1")) //고소장
	{
		ubi0.setJrfFileName("bra05_p01_1.jrf");

		ubi0.Retrieve();
		ubi0.directPrint();
	}
	else if(nCell == grd_result.GetBindCellIndex("body", "P2")) //위임장
	{
		ubi0.setJrfFileName("bra05_p01_2.jrf");

		ubi0.Retrieve();
		ubi0.directPrint();
	}
	else if(nCell == grd_result.GetBindCellIndex("body", "P3")) //진술서
	{
		//ubi0.setJrfFileName("bra05_p01_3.jrf");
		ubi0.setJrfFileName("show_image.jrf"); 
		ubi0.setArgument("ar_img_url", path + urlEncodeUtf8(ds_result.GetColumn(nRow, "ADVT_FILE")));

		ubi0.Retrieve();
		ubi0.directPrint();
	}
	else if(nCell == grd_result.GetBindCellIndex("body", "P4")) //최고서
	{
		/* 기존 최고서 양식 대신 본인들이 올린 파일로만 뽑겠다고 하여 고소등록 메뉴에서 첨부파일로 올리고 그 파일을 출력하는 형태로 변경함(2021-12-02)
		ds_upso.SetColumn(0, "UPSO_CD", ds_result.GetColumn(nRow, "UPSO_CD"));
		gfn_syncCall("svcBpapSel","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002007&EVENTID=sel_bpap","S=ds_upso","ds_upso_print=SEL4","","fn_CallBack");

		ubi0.SetArgument("UPSO_CD", ds_upso_print.GetColumn(0, "UPSO_CD"));

		if(length(ds_result.GetColumn(nRow, "PRINT_DATE")) == 8)
		{
			ubi0.SetArgument("SEND_DAY", ds_result.GetColumn(nRow, "PRINT_DATE"));
		}
		else
		{
			ubi0.SetArgument("SEND_DAY", ds_upso_print.GetColumn(0, "DISP_DAY"));
		}

		ubi0.SetVariable("exportAsOrgImage", "true");

		if(length(ds_upso_print.GetColumn(0, "NEW_DAY")) == 8)
		{
			ubi0.setJrfFileName(+"bra05_s05.jrf");
		}
		else
		{
			ubi0.setJrfFileName(+"bra05_s05_dig.jrf");
		}

		ubi0.Retrieve();
		ubi0.directPrint();*/
		ubi0.setJrfFileName("show_image.jrf"); 
		ubi0.setArgument("ar_img_url", path + urlEncodeUtf8(ds_result.GetColumn(nRow, "BPAP_FILE")));

		ubi0.Retrieve();
		ubi0.directPrint();
	}
	else if(nCell == grd_result.GetBindCellIndex("body", "P5")) //침해저작물
	{
		ubi0.SetArgument("PROD_CD", ds_result.GetColumn(nRow, "PROD_CD"));

		ubi0.setJrfFileName(+"bra05_p01_5.jrf");

		ubi0.Retrieve();
		ubi0.directPrint();
	}
	else if(nCell == grd_result.GetBindCellIndex("body", "P6")) //저작물등록정보
	{
		gv_Form_Args = "bra05_p01";
		gv_Form_Args2 = ds_result.GetColumn(nRow, "PROD_CD");

		gfn_OpenChildWindow("fi_opu::opu01_r03.xml","저작물조회","");
	}
	else if(nCell == grd_result.GetBindCellIndex("body", "P0")) //전체
	{
		// 고소장
		ubi0.setJrfFileName("bra05_p01_1.jrf");

		ubi0.Retrieve();
		ubi0.directPrint();

		// 위임장
		ubi0.SetArgument("ACCU_DAY", ds_result.GetColumn(nRow, "ACCU_DAY"));
		ubi0.SetArgument("ACCU_NUM", ds_result.GetColumn(nRow, "ACCU_NUM"));
		ubi0.SetArgument("ACCU_BRAN", ds_result.GetColumn(nRow, "ACCU_BRAN"));
		ubi0.SetArgument("PRINT_DATE", ds_result.GetColumn(nRow, "PRINT_DATE"));

		ubi0.setJrfFileName("bra05_p01_2.jrf");

		ubi0.Retrieve();
		ubi0.directPrint();

