		{
			ubi0.setJrfFileName("show_image.jrf"); 
			ubi0.setArgument("ar_img_url", path + urlEncodeUtf8(ds_result.GetColumn(nRow, "BPAP_FILE")));

			ubi0.Retrieve();
			ubi0.directPrint();
