		// 침해저작물
		if(length(ds_result.GetColumn(nRow, "PROD_CD")) > 0)
		{
			ubi0.SetArgument("PROD_CD", ds_result.GetColumn(nRow, "PROD_CD"));

			ubi0.setJrfFileName(+"bra05_p01_5.jrf");

			ubi0.Retrieve();
			ubi0.directPrint();

			gv_Form_Args = "bra05_p01";
			gv_Form_Args2 = ds_result.GetColumn(nRow, "PROD_CD");

			gfn_OpenChildWindow("fi_opu::opu01_r03.xml","저작물조회","");
		}
	}
}
