function bra05_s03_OnLoadCompleted(obj)
{
	// 센터코드 가져오기
	gfn_GetDataGIBU_CD(this.cob_BRAN_CD, 1);

	this.cob_accu_gbn.Index = 0;
	gfn_loadForm(obj);

	this.ds_dup_in.AddRow();
	ds_Pnt_in.AddRow();

	ds_search.ClearData();
	ds_search.AddRow();
	this.cob_accu_gbn.Index = 0;
	//초기화 => 검색조건과 결과값을 받는 데이터 셋을 같이 쓰므로 
	//         반드시 useClientLayout을 true로 설정 필요
	ds_find_code.UseClientLayout = true;
	ds_use_amt.UseClientLayout = true;

	// 입금구분 가져오기
	gfn_PubCode("ds_REPT_GBN", 2, "00141", "A");

	//판결내역 가져오기
	gfn_PubCode("ds_judge", 2, "00220");
	var iRow = ds_judge.InsertRow(0);
	ds_judge.SetColumn(iRow,"CODE_CD","");
	ds_judge.SetColumn(iRow,"CODE_NM","");

	this.ds_BRAN_CD.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.value);
	gfn_syncCall("SAWON_CLICK","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select","S=ds_BRAN_CD","ds_SawonInfo=SEL1","","fn_CallBack");	

	gfn_SetPermission(this.cob_BRAN_CD.value);
	gfn_SetInnerPermission("btn_low,btn_attorney,btn_cancel,btn_statement,btn_add,btn_del,btn_calc,btn_memo_save");

	if(gds_sessioninfo.GetColumn(0, "DEPT_CD") == "122040100" || gds_sessioninfo.GetColumn(0, "BRAN_CD") == "AL" )
	{
		btn_accu_del.Visible = true;
	}
}

function fn_CommmonButtonClick(strCode){

    //신규
    if(strCode == "NEW"){
		fn_New();
    }
    //조회
    else if(strCode == "SEARCH"){
		fn_Search();
    }
    //저장
    else if(strCode == "SAVE"){
		fn_Save();
    }
    //삭제
    else if(strCode == "DELETE"){
		//fn_Delete();
    }
    //Excel
    else if(strCode == "EXCEL"){
		//fn_Excel();
    }
    //출력
    else if(strCode == "PRINT"){
		//fn_Print();
    }
    //닫기
    else if(strCode == "CLOSE"){
		close();
    }

	return;
}

function fn_New() 
{
	// 입력 필수 항목 검사
	if (!gfn_RequiredCheck(this.cob_BRAN_CD, "센터코드를 선택하세요")) return;

	//초기화
	this.edt_upso_cd.Enable = true;
	this.edt_upso_cd.BKColor = "user13";

	this.edt_plcst_cd.BKColor = "user13";
	this.edt_plcst_nm.BKColor = "user13";

	this.btn_low.Visible = false;
	this.btn_attorney.Visible = false;
	this.btn_cancel.Visible = false;
	this.btn_statement.Visible =false;

	this.btn_search.Enable = true;

	//조회시 필수 항목/ 필수가 아닌 항목
	this.mae_accu_day.BKColor = "";
	this.mae_accu_num.BKColor = "";
	this.edt_accu_bran.BKColor = "";

	btn_attch_save.Enable = false;
	btn_attch_del.Enable = false;

	fn_NewInit();
}


function fn_NewInit()
{
	if (length(this.edt_upso_cd.Text) == 0) {
		return;
	}
	/*
	this.ds_dup_in.SetColumn(0, "UPSO_CD", this.edt_upso_cd.Text);
	gfn_SyncCall("sys_accu_dup_chk","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_dup_chk","S=ds_dup_in","ds_dup_out=SEL1","","fn_CallBack");
	if(this.ds_dup_out.GetColumn(0,"CNT")>0){
		gfn_Confirm("MB_OK", "이미 고소가 등록된 업소입니다.", "경고", "0");

		this.ds_upso_info.ClearData();
		this.ds_accu_detail.ClearData();
		this.ds_accu_detail.ClearData();
		var iRow = ds_upso_info.InsertRow(0);
		ds_upso_info.SetColumn(iRow,"CODE_CD","");
		ds_upso_info.SetColumn(iRow,"CODE_NM","");
		return;
	}
	*/

	// 고소정보가 존재하는 경우만 신규시 변경여부 체크
	/*
	if(ds_accu_detail.count > 0 ){
		var strMsg = "변경된 내역이 있습니다.\n 신규건을 생성하시겠습니까?";
		if(length(mae_ACCU_DAY_2.Value) > 0 && !gfn_changeAction("USER", "ds_accu_detail,ds_accu_memo", strMsg)) return;
	}
	*/

	ds_search.ClearData();
	ds_search.AddRow();
	ds_search.SetColumn(0, "BRAN_CD", this.cob_BRAN_CD.Value);
	ds_search.SetColumn(0, "UPSO_CD", this.edt_upso_cd.Text);
	mae_accu_day.Value = "";
	mae_accu_num.Value = "";
	edt_accu_bran.Text = "";
	cob_accu_gbn.Value = "";
	ds_accu_amt.ClearData();

	gfn_SyncCall("accu_init","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_init","S=ds_search","ds_upso_info=SEL2 ds_accu_amt=SEL6","","fn_CallBack");

	//고소정보 상세 초기화 처리
	ds_accu_detail.ClearData();
	ds_accu_detail.AddRow();
	ds_accu_detail.SetColumn(ds_accu_detail.currow, "ACCU_DAY", today());
	ds_accu_detail.SetColumn(ds_accu_detail.currow, "ACCU_BRAN", this.cob_BRAN_CD.Value);
	ds_accu_detail.SetColumn(ds_accu_detail.currow, "UPSO_CD", this.edt_upso_cd.Text);
	ds_accu_detail.SetColumn(ds_accu_detail.currow, "ACCU_GBN", "A");

	//업소정보의 현재 시작년월정보를 대상기간의 시작년월로 설정
	var FROM_YRMN = ds_upso_info.GetColumn(ds_upso_info.currow, "FROM_YRMN");
	ds_accu_detail.SetColumn(ds_accu_detail.currow, "START_YRMN", FROM_YRMN);

	//고소정보 메모 초기화 처리
	ds_accu_memo.ClearData();

	cob_accu_gbn.Value = "A";  		// 법구분=> A:형사, B:민사 
	this.cob_accu_obj.Value = "B";
	fn_display_accuGbn();			//고소구분별 화면 표시 항목 변경


	cal_COMPN_DAY.BKColor = "";
	cal_COMPN_DAY.Enable = false;
	cal_SOL_START.BKColor = "";
	cal_SOL_START.Enable = false;
	cal_SOL_END.BKColor = "";
	cal_SOL_END.Enable = false;
	mae_sol_org_amt.BKColor = "";
	mae_sol_addt_amt.BKColor = "";

	// 입금마감이 끝난 고소해결 정보중 new를 한경우 enabled를 true로 바꿔줘야 한다. 02010/3/29
	edt_plcst_cd.Enable = true;
	//cal_START_YRMN.Enable = true; //2018-12-06 태진
	cal_END_YRMN.Enable = true;

	btn_calc.Enable = true;
	cob_accu_gbn2.Enable = true;
	edt_EVT_NUM.Enable = true;
	edt_EVT_NUM2.Enable = true;
	cob_JUDG_CD.Enable = true;
	edt_JUDG_BRE.Enable = true;
	cob_accu_obj.Enable = true;
	edt_INSPECTOR.Enable = true;
	mae_sol_org_amt.Enable = true;
	mae_sol_addt_amt.Enable = true;
	mae_comis.Enable = true;
	btn_AccuInfo.Enable = true;

	edt_Prod.Enable = true;


	FV_MODE = "NEW";
	FV_MODE2 = "NEW";

	c_search = "N";

	v_flag = "n";

}
function fn_Search() {

	if (this.edt_upso_cd.Enable) {
		if(Confirm("등록을 취소하시겠습니까?")) {
			this.edt_upso_cd.Enable = false;
			this.edt_upso_cd.BKColor = "";
			edt_plcst_cd.Enable = false;
			edt_plcst_cd.BKColor = "";
			edt_plcst_nm.BKColor = "";
			mae_accu_day.BKColor = "user13";
			mae_accu_num.BKColor = "user13";
			edt_accu_bran.BKColor = "user13";
		}
		else {
			return;
		}
	}

	if (!gfn_CheckFormNull(this)) return;
	fn_search_m();
}

function fn_Search_m() 
{
	this.edt_upso_cd.BKColor = "";
	this.edt_upso_cd.Enable = false;
	//this.edt_plcst_cd.BKColor = "";
	//this.edt_plcst_nm.BKColor = "";
	//this.edt_plcst_cd.BKColor = "user13";

	if(length(mae_ACCU_DAY_2.Value) > 0 && !gfn_changeAction("SEARCH", "ds_accu_detail,ds_accu_memo")){
		 return;	
	}

	ds_search.SetColumn(0, "BRAN_CD", this.cob_BRAN_CD.Value);
	ds_search.SetColumn(0, "ACCU_DAY", this.mae_accu_day.Value);
	ds_search.SetColumn(0, "ACCU_NUM", this.mae_accu_num.Value);
	ds_search.SetColumn(0, "ACCU_BRAN", this.edt_accu_bran.Text);
	ds_search.SetColumn(0, "ACCU_GBN", this.cob_accu_gbn.Value);

//trace(this.ds_search.SaveXML());

	gfn_SyncCall("accu_detail","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_detail",
	"S=ds_search","ds_upso_info=SEL1 ds_accu_detail=SEL2 ds_accu_memo=SEL3 ds_accu_amt=MRG9","","fn_CallBack");
	//trace(ds_accu_amt.SaveXML());
	// 고소대상기간 중복 방지를 위해 기존 저장된 대상기간을 저장해 놓는다. 03/23
	check_start = this.ds_accu_detail.GetColumn(0,"START_YRMN");
	check_end = this.ds_accu_detail.GetColumn(0,"END_YRMN");

	//trace(this.ds_upso_info.SaveXML());

	if(	this.ds_accu_amt.RowCount() == 0 || 
		length(this.mae_CLSBS_YRMN.Value) > 0) {
		 fn_display_curAmt(false);
	}
	else {
		fn_display_curAmt(true);
	}

	b_cal_SOL_START    = this.cal_SOL_START.Value;
	b_cal_SOL_END      = this.cal_SOL_END.Value;
	b_mae_sol_tot_amt  = this.mae_sol_tot_amt.Value;
	b_mae_sol_org_amt  = this.mae_sol_org_amt.Value;
	b_mae_sol_addt_amt = this.mae_sol_addt_amt.Value;
	b_mae_comis        = this.mae_comis.Value;
	b_compn_day		   = this.cal_COMPN_DAY.Value;
	b_rept_gbn         = this.cob_PERT_GBN.Value;
	c_judg_cd          = this.cob_JUDG_CD.Value;
	c_search           = "Y";  //서치정보를 저장해 논다.

	CHK_NAME_O = ds_upso_Info.GetColumn(0,"PERMMSTR_NM");
	CHK_JUMIN_O = ds_upso_Info.GetColumn(0,"PERMMSTR_RESINUM");

	CHK_NAME_B = ds_upso_Info.GetColumn(0,"MNGEMSTR_NM");
	CHK_JUMIN_B = ds_upso_Info.GetColumn(0,"MNGEMSTR_RESINUM");

	//고소대상이 경영주면 경영주 정보를, 허가주이면 허가주 정보를 보여준다.
	if(this.ds_accu_detail.GetColumn(0,"ACCU_OBJ") == "O"){ // 경영주
		edt_MNGEMSTR_NM.Text = CHK_NAME_O;
		mae_MNGEMSTR_RESINUM = CHK_JUMIN_O;

	}else{
		edt_MNGEMSTR_NM.Text = CHK_NAME_B;
		mae_MNGEMSTR_RESINUM = CHK_JUMIN_B;
	}

	if(this.cob_JUDG_CD.Value==0){
		cob_JUDG_CD_OnChanged(0,0);
	}

//조회 후에 고소 출력, 취소, 위임장, 진술서 쿼리를 보네기 위한 값을 입력한다.
	this.ds_Pnt_in.SetColumn(0, "UPSO_CD", this.edt_upso_cd.Text);
	this.ds_Pnt_in.SetColumn(0, "ACCU_DAY", this.mae_ACCU_DAY_2.value );
	this.ds_Pnt_in.SetColumn(0, "ACCU_NUM", this.mae_ACCU_NUM_2.Text );
	this.ds_Pnt_in.SetColumn(0, "ACCU_BRAN", this.edt_ACCU_BRAN_2.Text );
	this.ds_Pnt_in.SetColumn(0, "ACCU_GBN", this.cob_accu_gbn2.Value);

	//고소가 등록되면 고소장, 위임장 버튼 출력버튼 보이게
	if(length(this.mae_ACCU_DAY_2.Text) > 0){
		this.btn_low.Visible = true;	
		this.btn_attorney.Visible = true;
	}

	if(this.cob_JUDG_CD.value == '1' and this.cal_COMPN_DAY.Value != "" ) {
		this.btn_cancel.Visible = true;
		this.btn_statement.Visible = true;
	}else{
	    this.btn_cancel.Visible = false;
		this.btn_statement.Visible = false;
	}

	//조회 결과가 없어도 빈 레코드 하나 추가 필요 => 신규입력을 위한 업소코드 입력을 위하여
	if(ds_upso_info.count == 0 ) {
		ds_upso_info.AddRow();
		// 고소번호가 조회조건으로 지정되고 센터가 잘 못 지정되면 고소메모만 조회되는 경우 존재
		// 고소메모는 업소정보와 고소정보가 없으면 무의미하므로 clear
		if(ds_accu_memo.count > 0) {	
			ds_accu_memo.ClearData();
		}
		return;
	}

	fn_search_after();		//조회결과에 대한 화면 추가 처리
	COMPN_DAY_INIT = cal_COMPN_DAY.value;
	FV_MODE2 = "SEARCH";

	var strCode = cob_JUDG_CD.Value;

	if (strCode == "") {
		this.cal_COMPN_DAY.Enable = false;

		this.cal_COMPN_DAY.BKColor		= "";
		this.cal_SOL_START.BKColor		= "";
		this.cal_SOL_END.BKColor		= "";
		this.mae_sol_org_amt.BKColor	= "";
		this.mae_sol_addt_amt.BKColor	= "";

		this.cal_COMPN_DAY.Value = "";
		cal_SOL_START.Value = cal_START_YRMN.Value;
		cal_SOL_END.Value = "";
		mae_sol_tot_amt.Value = "";
		mae_sol_org_amt.Value = "";
		mae_sol_addt_amt.Value = "";
		mae_comis.Value = "";
	}
	else {
		this.cal_COMPN_DAY.Enable = true;

//		mae_sol_org_amt.Enable = false;
//		mae_sol_addt_amt.Enable = false;
	}

	//판결내역이 취하입금 혹은 벌금일 경우 완결일자의 내역을 입력불가

	if(this.cob_JUDG_CD.Value == 1 || this.cob_JUDG_CD.Value == 4) {
		this.cal_COMPN_DAY.Enable = false;
	}
	else {
		this.cal_COMPN_DAY.Enable = true;
	}

	this.btn_search.Enable = false;


	if(length(ds_accu_detail.GetColumn(0, "COMPN_DAY2")) > 0)
	{
		if(length(ds_accu_detail.GetColumn(0, "COMPN_DAY")) > 0)
		{
			btn_Kian.Visible = false;
		}
		else
		{
			btn_Kian.Visible = true;
		}

		ds_state_search.SetColumn(0, "LNK_KEY", ds_accu_detail.GetColumn(0, "ACCU_DAY") + "-" + ds_accu_detail.GetColumn(0, "ACCU_NUM") + "-" + ds_accu_detail.GetColumn(0, "ACCU_BRAN"));
		gfn_syncCall("SEARCH_STAT","KOMCA?SYSID=PATHFINDER&MENUID=1000002002005&EVENTID=sel_gw_apprv_state","S=ds_state_search","ds_approval_result=SEL1","","fn_CallBack");
	}
	else
	{
		ds_state_search.SetColumn(0, "LNK_KEY", "");
		ds_approval_result.ClearData();
	}
	fn_SearchEnable();

	v_flag = "s";
}

function fn_Save() 
{
	// 입력 필수 항목 검사;
	if (!gfn_CheckFormNull(this)) return;

	if(edt_upso_cd.Text != ds_accu_detail.GetColumn(0, "UPSO_CD") || length(edt_upso_cd.Text) != 8 || length(ds_accu_detail.GetColumn(0, "UPSO_CD")) != 8 || ds_accu_detail.rowcount < 1)
	{
		alert("업소코드가 변경되었습니다.\n업소 조회 후 다시 등록하여주시기 바랍니다.");
		return;
	}

	//해결전에 입금을 미리 잡고 계산누른 경우를 방지
	if(mae_req_tot_amt.Text == "0" || toNumber(mae_req_tot_amt.Text) < 1)
	{
		alert("의뢰총금액이 0원입니다.\n고소해결인 경우, 입금을 먼저 잡은 뒤에 계산버튼을 눌렀을 수 있으니, 내역을 다시 조회하여 해결내역을 저장하여주시기 바랍니다.");
		return;
	}

	//고소의 경우 입금마감 필요 없을 듯.
	ds_search.ClearData();
	ds_search.AddRow();
	ds_search.SetColumn(0, "BRAN_CD" , this.cob_BRAN_CD.Value);
	ds_search.SetColumn(0, "ACCU_DAY", this.cal_COMPN_DAY.Value);

	gfn_SyncCall("accu_mng","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=bran_rept_end","S=ds_search","ds_closingYn=SEL1","","fn_CallBack");

	if(ds_closingYn.GetColumn(0, "CNT") != "0"){
        gfn_Confirm("MB_OK", "해당기간중 마감내역이 존재합니다. 마감내역을 확인하세요.\n사건진행과정 메모만 수정시에는 사건진행과정 버튼을 사용하세요.", "경고", "0");
	    return;
	}

	//판결 내역(취하:입금 or 구약식(해결)이 있으나 완결일자가 없을 경우 

	//alert("1 : "+this.cob_JUDG_CD.Value);
	if(this.cob_judg_cd.Value=="1" || this.cob_JUDG_CD.Value == "41"){

		if(length(toString(this.cal_COMPN_DAY.Value))==0){
			gfn_Confirm("MB_OK", "완결일자를 입력해주세요.", "경고", "0");
			return;
		}
	}

	/*

	//완결일자가 있을 경우 판결내역이 없는 경우
	if(length(toString(this.cal_COMPN_DAY.Value)) > 0){
		if(this.cob_judg_cd.Value>0){
			gfn_Confirm("MB_OK", "판결내역을 선택해 주세요.", "경고", "0");
			return;
		}
	}

	*/


	if(length(this.mae_req_tot_amt.Text) ==0){
        gfn_Confirm("MB_OK", "고소처리 정보의 계산을 눌러주세요.", "경고", "0");
		this.Button0.SetFocus();
		return false;
	}
	//완결일자가 있을경우 판결내역이 선택되어야한다. 
	if(length(this.cal_COMPN_DAY.Value)>0 and length(this.cob_JUDG_CD.Value)==0 ){
        gfn_Confirm("MB_OK", "완결일자가 입력되었을 경우 \n판결내역을 입력해야합니다.", "경고", "0");
		this.cob_JUDG_CD.SetFocus();
		return false;
	}


	// 신규입력인 경우 대상기간 및 의뢰관련 금액 필수입력 체크 처리 필요
	//if( FV_MODE == "NEW" ) {
	if(v_flag == "n")
	{
		if( length(ds_accu_detail.GetColumn(ds_accu_detail.currow, "ACCU_GBN")) == 0 ){
            gfn_Confirm("MB_OK", "고소구분을 설정하세요.", "경고", "0");
			return;
		}
		if( length(ds_accu_detail.GetColumn(ds_accu_detail.currow, "START_YRMN")) == 0 
		    || length(ds_accu_detail.GetColumn(ds_accu_detail.currow, "END_YRMN")) == 0 ){
            gfn_Confirm("MB_OK", "대상기간이 올바로 설정되지 않았습니다.", "경고", "0");
			return;
		}
		if( length(ds_accu_detail.GetColumn(ds_accu_detail.currow, "REQ_ORG_AMT")) == 0 
		    || length(ds_accu_detail.GetColumn(ds_accu_detail.currow, "REQ_ADDT_AMT")) == 0 ){
            gfn_Confirm("MB_OK", "의뢰원금 또는 의뢰가산금이 미설정되었습니다.", "경고", "0");
			return;
		}	
	}

	FV_MODE = "";

	// 완결일자가 존재할 경우 다음의 경우는 저장불가 처리
	// 판결코드가 취하:입금(1) 인 경우 => 해결시작년월, 해결종료년월, 해결총금액이 반드시 존재
	if( length(cal_COMPN_DAY.Value) > 0 )
	{
		if( length(edt_judg_cd.Text) == 0 )
		{
            gfn_Confirm("MB_OK", "완료건에 대하여 판결코드를 설정해야 저장할 수 있습니다.", "경고", "0");
			return;
		}
		if( edt_judg_cd.Text == "1" )
		{
			if( length(cal_SOL_START.Value) == 0 || length(cal_SOL_END.Value) == 0 )
			{
                gfn_Confirm("MB_OK", "판결코드가 취하:입금인 왼료건은 해결기간을 입력해야 합니다.", "경고", "0");
				return;
			}
			if( length(mae_sol_tot_amt.Value) == 0 || mae_sol_tot_amt.Value == 0 )
			{
                gfn_Confirm("MB_OK", "판결코드가 취하:입금인 왼료건은 해결관련 금액정보를 입력해야 합니다.", "경고", "0");
				return;
			}
		}
		if(toNumber(this.cal_SOL_START.Value) > toNumber(this.cal_SOL_END.Value))
		{
			gfn_Confirm("MB_OK", "해결일자 기간의 시작은 종료기간보다 클 수 없습니다.", "경고", "0");
			return false;
		}
	}

	// 신규모드일 경우 고소기간 중복 체크 한다.
	//if(FV_MODE = "NEW" && FV_MODE2 = "NEW")
	if(v_flag == "n")
	{
		this.ds_dup_in.SetColumn(0, "UPSO_CD", this.edt_upso_cd.Text);
		this.ds_dup_in.SetColumn(0, "START_YRMN", substr(this.cal_START_YRMN.Value,0,6));
		this.ds_dup_in.SetColumn(0, "END_YRMN", substr(this.cal_END_YRMN.Value,0,6));

		gfn_SyncCall("sys_accu_dup_chk","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_dup_chk","S=ds_dup_in","ds_dup_out=SEL1","","fn_CallBack");

		if(this.ds_dup_out.GetColumn(0,"CNT")>0){
			gfn_Confirm("MB_OK", "해당기간에 이미 고소가 등록된 업소입니다.", "경고", "0");

			return;
		}

		//해결되지 않은 고소가 남아있는지 여부 확인
		//고소기간이 다르더라도 이전 고소내역이 해결확정까지 되지 않았으면 등록을 보류한다 2018-12-06 류선희대리 요청
		gfn_SyncCall("sys_accu_comp_chk","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_comp_chk","S=ds_dup_in","ds_comp_chk=SEL1","","fn_CallBack");

		if(ds_comp_chk.GetColumn(0,"CNT") > 0){
			gfn_Confirm("MB_OK", "해결되지 않은 고소가 있습니다.", "경고", "0");

			return;
		}
	}
	else
	{
		// 수정모드일 경우 대상기간을 수정했을 경우 도 중복체크를 한다.
		if(check_start != this.cal_START_YRMN.Value || check_end != this.cal_END_YRMN.Value)
		{
			this.ds_dup_in.SetColumn(0, "UPSO_CD", this.edt_upso_cd.Text);
			this.ds_dup_in.SetColumn(0, "START_YRMN", substr(this.cal_START_YRMN.Value,0,6));
			this.ds_dup_in.SetColumn(0, "END_YRMN", substr(this.cal_END_YRMN.Value,0,6));
			this.ds_dup_in.SetColumn(0, "ACCU_DAY", this.ds_accu_detail.GetColumn(0,"ACCU_DAY"));
			this.ds_dup_in.SetColumn(0, "ACCU_NUM", this.ds_accu_detail.GetColumn(0,"ACCU_NUM"));
			this.ds_dup_in.SetColumn(0, "ACCU_BRAN", this.ds_accu_detail.GetColumn(0,"ACCU_BRAN"));

			//trace(this.ds_dup_in.SaveXML());

			gfn_SyncCall("sys_accu_dup_chk","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_dup_chk","S=ds_dup_in","ds_dup_out=SEL1","","fn_CallBack");

			if(this.ds_dup_out.GetColumn(0,"CNT")>0)
			{
				gfn_Confirm("MB_OK", "해당기간에 이미 고소가 등록된 업소입니다.", "경고", "0");

				return;
			}
		}
	}


	if(gfn_Confirm("MB_YESNO", "저장 하시겠습니까?") == "6"){

		this.ds_accu_detail.SetColumn(0,"STAFF_CD", this.ds_upso_info.GetColumn(0,"STAFF_CD"));
		this.ds_accu_detail.SetColumn(0,"BRAN_CD", this.cob_BRAN_CD.Value);

		//trace(this.ds_accu_detail.SaveXML());


		gfn_SyncCall("svcSave","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_mng",
		"S=ds_accu_detail:U S1=ds_accu_memo:U","ds_accu_detail=SEL42 ds_accu_memo=SEL45","","fn_CallBack", gv_TraceLog, GV_FORMID);

		this.btn_low.Visible = true;
		this.btn_attorney.Visible = true;



		if(this.cob_judg_cd.Text == "1" && this.cal_COMPN_DAY.Value != "" ) {
			this.btn_cancel.Visible = true;
			this.btn_statement.Visible = true;
		}
		else{
		    this.btn_cancel.Visible = false;
			this.btn_statement.Visible = false;
		}
	}
	FV_MODE2 = "SEARCH";
}

function fn_Excel() {}

function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0) {
		//에러 공통 팝업
		gfn_SetErrorPop(ErrorCode,ErrorMsg);
		return;
	}	

	if (srvId == "accu_detail") {
		gfn_SetStatusMsg("정상적으로 조회되었습니다.");

		gfn_SyncCall("accu_attch_sel","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=sel_accu_attch","S=ds_accu_detail","ds_advt_attch=SEL1 ds_bpap_attch=SEL2 ds_evtd_attch=SEL3 ds_comp_attch=SEL4","","fn_CallBack");
	}
	else if (srvId == "accu_mng"){
	 	gfn_SetStatusMsg(ErrorMsg);
		if(length(ErrorMSG) >0){
			gfn_Confirm("MB_OK", ErrorMsg, "경고", "0");
		}
	}
	else if (srvId == "svcSave"){
	 	gfn_SetStatusMsg(ErrorMsg);
		if(length(ErrorMSG) >0){
			gfn_Confirm("MB_OK", ErrorMsg, "경고", "0");
		}
		else
		{
			this.mae_accu_day.Value = mae_ACCU_DAY_2.Value;
			this.mae_accu_num.Value = mae_ACCU_NUM_2.Value;
			this.edt_accu_bran.Text = edt_ACCU_BRAN_2.Text; // 저장 후 값 clear 되지 않도록 설정 2018.01.09 이다섭
			alert("저장되었습니다.");

			fn_Search_m();
		}
	}
	else if (srvId == "accu_delete") {
		mae_accu_day.Value = "";
		mae_accu_num.Value = "";
		edt_accu_bran.Text = "";
		cob_accu_gbn.Index = 0;
		//ds_upso_info.ClearData();
	}
	else if (srvId == "svcSave_file")
	{
		gfn_SyncCall("accu_attch_sel","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=sel_accu_attch","S=ds_accu_detail","ds_advt_attch=SEL1 ds_bpap_attch=SEL2 ds_evtd_attch=SEL3 ds_comp_attch=SEL4","","fn_CallBack");
	}
	else if (srvId == "svcDel_file")
	{
		gfn_SyncCall("accu_attch_sel","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=sel_accu_attch","S=ds_accu_detail","ds_advt_attch=SEL1 ds_bpap_attch=SEL2 ds_evtd_attch=SEL3 ds_comp_attch=SEL4","","fn_CallBack");
	}
	else if (srvId == "accu_attch_sel")
	{
		if(ds_advt_attch.rowcount > 0)
		{
			btn_advt_down.Enable = true;
			btn_advt_del.Enable = true;
		}
		else
		{
			btn_advt_down.Enable = false;
			btn_advt_del.Enable = false;
		}
		if(ds_bpap_attch.rowcount > 0)
		{
			btn_bpap_down.Enable = true;
			btn_bpap_del.Enable = true;
		}
		else
		{
			btn_bpap_down.Enable = false;
			btn_bpap_del.Enable = false;
		}
		if(ds_evtd_attch.rowcount > 0)
		{
			btn_evtd_del.Enable = true;
		}
		else
		{
			btn_evtd_del.Enable = false;
		}
		if(ds_comp_attch.rowcount > 0)
		{
			btn_comp_down.Enable = true;
		}
		else
		{
			btn_comp_down.Enable = false;
		}	
	}
	else if(srvId == "svcDelete")
	{
		fn_New();
		alert("고소내역이 삭제되었습니다.");
	}
}

function fn_SearchEnable()
{
	// 완결일을 확인하여 입금마감된 경우 전체 수정 불가
	if (length(this.ds_accu_detail.GetColumn(0, "COMPN_DAY2")) > 0) {
		ds_search.ClearData();
		ds_search.AddRow();
		ds_search.SetColumn(0, "BRAN_CD" , this.cob_BRAN_CD.Value);
		ds_search.SetColumn(0, "ACCU_DAY", this.ds_accu_detail.GetColumn(0, "COMPN_DAY2"));

		if(gfn_DocIsFinal(ds_approval_result.GetColumn(0, "CODE_CD")))
		{
			this.cob_accu_gbn2.Enable = false;
			this.edt_EVT_NUM.Enable = false;
			this.edt_EVT_NUM2.Enable = false;
			this.cob_JUDG_CD.Enable = false;
			this.edt_JUDG_BRE.Enable = false;
			this.cob_accu_obj.Enable = false;
			this.edt_INSPECTOR.Enable = false;
			this.cal_SOL_START.Enable = false;
			this.cal_SOL_END.Enable = false;
			this.mae_sol_org_amt.enable = false;
			this.mae_sol_addt_amt.enable = false;
			this.edt_plcst_cd.Enable = false;
			//this.edt_plcst_nm.Enable = false;
			this.btn_AccuInfo.Enable = false;
			this.cal_START_YRMN.Enable = false;
			this.cal_END_YRMN.Enable = false;
			this.btn_calc.Enable = false;
			this.mae_comis.Enable = false;

			edt_Prod.Enable = false;
		}
		else{
			gfn_SyncCall("accu_mng","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=bran_rept_end","S=ds_search","ds_closingYn=SEL1","","fn_CallBack");

			if(ds_closingYn.GetColumn(0, "CNT") != "0"){
				this.cob_accu_gbn2.Enable = false;
				this.edt_EVT_NUM.Enable = false;
				this.edt_EVT_NUM2.Enable = false;
				this.cob_JUDG_CD.Enable = false;
				this.edt_JUDG_BRE.Enable = false;
				this.cob_accu_obj.Enable = false;
				this.edt_INSPECTOR.Enable = false;
				this.cal_SOL_START.Enable = false;
				this.cal_SOL_END.Enable = false;
				this.mae_sol_org_amt.enable = false;
				this.mae_sol_addt_amt.enable = false;
				this.edt_plcst_cd.Enable = false;
				//this.edt_plcst_nm.Enable = false;
				this.btn_AccuInfo.Enable = false;
				this.cal_START_YRMN.Enable = false;
				this.cal_END_YRMN.Enable = false;
				this.btn_calc.Enable = false;
				this.mae_comis.Enable = false;

				edt_Prod.Enable = false;
			}	
			else {
				this.cob_accu_gbn2.Enable = true;
				this.edt_EVT_NUM.Enable = true;
				this.edt_EVT_NUM2.Enable = true;
				this.cob_JUDG_CD.Enable = true;
				this.edt_JUDG_BRE.Enable = true;
				this.cob_accu_obj.Enable = true;
				this.edt_INSPECTOR.Enable = true;
				//this.cal_SOL_START.Enable = true;
				this.cal_SOL_END.Enable = true;
				this.mae_sol_org_amt.enable = true;
				this.mae_sol_addt_amt.enable = true;
				this.edt_plcst_cd.Enable = true;
				//this.edt_plcst_nm.Enable = true;
				this.btn_AccuInfo.Enable = true;
				//this.cal_START_YRMN.Enable = true; //2018-12-06 태진
				this.cal_END_YRMN.Enable = true;
				this.btn_calc.Enable = true;
				this.mae_comis.Enable = true;

				edt_Prod.Enable = true;
			}
		}
	}
	else {
		this.cob_accu_gbn2.Enable = true;
		this.edt_EVT_NUM.Enable = true;
		this.edt_EVT_NUM2.Enable = true;
		this.cob_JUDG_CD.Enable = true;
		this.edt_JUDG_BRE.Enable = true;
		this.cob_accu_obj.Enable = true;
		this.edt_INSPECTOR.Enable = true;
		//this.cal_SOL_START.Enable = true;
		this.cal_SOL_END.Enable = true;
		this.mae_sol_org_amt.enable = true;
		this.mae_sol_addt_amt.enable = true;
		this.edt_plcst_cd.Enable = true;
		//this.edt_plcst_nm.Enable = true;
		this.btn_AccuInfo.Enable = true;
		//this.cal_START_YRMN.Enable = true; //2018-12-06 태진
		this.cal_END_YRMN.Enable = true;
		this.mae_comis.Enable = true;

		edt_Prod.Enable = true;

		// 의뢰일을 확인하여 입금마감된 경우 고소 대상기간 수정 불가
		if (length(this.ds_accu_detail.GetColumn(0, "REQ_DAY")) > 0) {
			ds_search.ClearData();
			ds_search.AddRow();
			ds_search.SetColumn(0, "BRAN_CD" , this.cob_BRAN_CD.Value);
			ds_search.SetColumn(0, "ACCU_DAY", this.ds_accu_detail.GetColumn(0, "REQ_DAY"));

			gfn_SyncCall("accu_mng","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=bran_rept_end","S=ds_search","ds_closingYn=SEL1","","fn_CallBack");

			if(ds_closingYn.GetColumn(0, "CNT") != "0"){
				this.cal_START_YRMN.Enable = false;
				this.cal_END_YRMN.Enable = false;
				this.btn_calc.Enable = false;
			}	
			else {
				//this.cal_START_YRMN.Enable = true; //2018-12-06 태진
				this.cal_END_YRMN.Enable = true;
				this.btn_calc.Enable = true;
			}
		}
	}
	btn_attch_save.Enable = true;
}

/*
	 * 기      능 : 	조회후  경찰서 컬럼 초기화
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function fn_search_after()
{
	if( ds_upso_info.count == 0) return;

	if( ds_accu_detail.count == 0) return;

	fn_display_accuGbn();	//고소구분별 화면 표시 항목 변경


	if( cob_PERT_GBN.value != "03" ) {
		//mae_comis.Enable = true;
	}

	plcst_cd = "";
	plcst_nm = "";
	low_cd = "";
	low_nm = "";	
}


/*
	 * 기      능 : 	경찰서/법원의 선택에 따른 UI변경
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function fn_display_accuGbn(){
	/* 고소구분이 B(민사)이면 
	    - 경찰서=>법원
	    - 담당조사관 관련 항목 invisible
	    - 현재시점 기준 고소금액항목 invisible    */
	//if( ds_accu_detail.GetColumn(ds_accu_detail.currow,"ACCU_GBN") == "B" ){
	if( this.cob_accu_gbn2.Value == "B" ){
		st_PLCST.Text = "법 원";
		st_INSPECTOR.Visible = false;
		edt_INSPECTOR.Visible = false;
		fn_display_curAmt(false);
	}else{
		st_PLCST.Text = "경찰서";
		st_INSPECTOR.Visible = true;
		edt_INSPECTOR.Visible = true;
		// 완결일자가 존재하면 현재시점 기준 고소금액항목 invisible
		if( length(ds_accu_detail.GetColumn(ds_accu_detail.currow,"COMPN_DAY2")) > 0 ){
			fn_display_curAmt(false);
		}else{
			fn_display_curAmt(true);
		}
	}
}


/*
	 * 기      능 : 	현재시점 기준 고소금액항목 UIvisible/invisible 처리함수
						- 현재기간/현재총금액/현재원금/현재가산금  		
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function fn_display_curAmt(flag)
{
	st_cur_term.Visible = flag;
	cal_FROM_YRMN.Visible = flag;
	st_cur_term_symbol.Visible = flag;
	cal_TO_YRMN.Visible = flag;
	st_cur_tot_amt.Visible = flag;
	mae_cur_tot_amt.Visible = flag;
	st_cur_org_amt.Visible = flag;
	mae_cur_org_amt.Visible = flag;
	st_cur_addt_amt.Visible = flag;
	mae_cur_addt_amt.Visible = flag;
}


/*
	 * 기      능 :  콤보의 선택값에 따른 수수료 변화
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function cob_PERT_GBN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if( strCode == "03" ){		// 무통장이면 수수료 항목 disable
		//mae_comis.Enable = false;
		mae_comis.value = 0;	// 초기화
	}else{
		//mae_comis.Enable = true;		
	}
}


/*
	 * 기      능 :  //고소대상 변경시
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function cob_accu_obj_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	// 고소대상 콤보박스에서 경영주나 허가주를 선택하면 해당 이름을 표시
	if(strCode=="O"){
		edt_MNGEMSTR_NM.Text = CHK_NAME_O;
		mae_MNGEMSTR_RESINUM = CHK_JUMIN_O;
	}else{
		edt_MNGEMSTR_NM.Text = CHK_NAME_B;
		mae_MNGEMSTR_RESINUM = CHK_JUMIN_B;
	}

}

/*
	 * 기      능 :  //콤보를 형사/민사 선택시 UI변경 
	 * 인      수 :  STRcode: 콤보의 코드값
	 * Return    : 	
	 * 예      시 :	
*/
