//대상기간 기간변경에 따른 의뢰금액 조회처리
function fn_getRequestAmt()
{
	if( length(cal_START_YRMN.Value) == 0 || length(cal_END_YRMN.Value) == 0 ) return;

	ds_use_amt.ClearData();
	ds_use_amt.AddRow();
	ds_use_amt.SetColumn(0, "UPSO_CD", ds_upso_info.GetColumn(ds_upso_info.currow,"UPSO_CD"));
	ds_use_amt.SetColumn(0, "START_YRMN", substr(cal_START_YRMN.Value,0,6));
	ds_use_amt.SetColumn(0, "END_YRMN", substr(cal_END_YRMN.Value,0,6));
	ds_use_amt.SetColumn(0, "ACCU_DAY", ds_accu_detail.GetColCount(0, "ACCU_DAY"));

	gfn_SyncCall("use_amt","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=use_amt","S=ds_use_amt","ds_use_amt=MRG7","","fn_CallBack");

	if( ds_use_amt.count > 0 ){
		ds_accu_detail.SetColumn(ds_accu_detail.currow,"REQ_ORG_AMT",ds_use_amt.GetColumn(0,"REQ_ORG_AMT"));
		ds_accu_detail.SetColumn(ds_accu_detail.currow,"REQ_ADDT_AMT",ds_use_amt.GetColumn(0,"REQ_ADDT_AMT"));
		ds_accu_detail.SetColumn(ds_accu_detail.currow,"REQ_TOT_AMT",ds_use_amt.GetColumn(0,"REQ_TOT_AMT"));	
	}

}
/* ============================================================================================
    2009.08.14  대상기간에 해당하는 업소의 사용료를 별도로 구해서 의뢰금액을 계산해야 하며
                계산된 의뢰금액은 변경이 불가하도록 요건이 변경되어 의뢰금액 계산은 서버에서
                가져오도록 변경했으므로 아래의 코드는 주석처리

// 의뢰원금 변경시
function mae_req_org_amt_OnChanged(obj,strText)
{
	var addt_amt = parseInt(mae_req_org_amt.value) * 0.3;				// 가산금 계산 => 원금 * 0.3
	mae_req_addt_amt.Value = addt_amt;

	var tot_amt = parseInt(mae_req_org_amt.value) + addt_amt;			// 총금액 = 원금 + 가산금 
	ds_accu_detail.SetColumn(ds_accu_detail.currow,"REQ_TOT_AMT",tot_amt);	
}
// 의뢰가산금 변경시
function mae_req_addt_amt_OnChanged(obj,strText)
{
	var addt_amt = parseInt(mae_req_addt_amt.value);
	var org_amt = parseInt(mae_req_org_amt.value);		

	var tot_amt = org_amt + addt_amt;	// 총금액 = 원금 + 가산금 
	ds_accu_detail.SetColumn(ds_accu_detail.currow,"REQ_TOT_AMT",tot_amt);	
}
//대상기간 기간변경 공통처리
function fn_changeReqTerm()
{
	if( length(cal_START_YRMN.Value) == 0 || length(cal_END_YRMN.Value) == 0 ) return;

	// 해당기간 개월수 취득
	var mon_cnt = fn_monthsBetween(cal_START_YRMN.Value, cal_END_YRMN.Value);

	//현재 업소의 월사용료 취득
	var MONPRNCFEE = ds_upso_info.GetColumn(ds_upso_info.currow,"MONPRNCFEE");

	var org_amt  = parseInt(MONPRNCFEE) * mon_cnt;	// 원금 계산 => 월사용료 * 해당기간 개월수
	var addt_amt = org_amt * 0.3;					// 가산금 계산 => 원금 * 0.3

	mae_req_org_amt.value = org_amt;
	mae_req_addt_amt.value = addt_amt;
	mae_req_tot_amt.value = org_amt + addt_amt;  	// 총금액 = 원금 + 가산금 		
}
============================================================================================*/
//================== 의뢰금액 관련 이벤트 처리 END =====================================


