//해결기간 기간변경 공통처리
function fn_changeSolTerm()
{
	if( length(cal_SOL_START.Value) == 0 || length(cal_SOL_END.Value) == 0 ) return;

	// 혐의없음인 경우 금액이 입력 안되도록 해달라는 요청 2018-08-01 박성호 요청
	if(cob_JUDG_CD.Value == "6") return;

	// 해당기간 개월수 취득
	var mon_cnt = fn_monthsBetween(cal_SOL_START.Value, cal_SOL_END.Value);

	//현재 업소의 월사용료 취득
	var MONPRNCFEE = ds_upso_info.GetColumn(ds_upso_info.currow,"MONPRNCFEE");

	var org_amt  = parseInt(MONPRNCFEE) * mon_cnt;	// 원금 계산 => 월사용료 * 해당기간 개월수

	mae_sol_org_amt.Value = org_amt;
	// 해결가산금은 수기 입력
	mae_sol_tot_amt.Value = org_amt + parseInt(mae_sol_addt_amt.Value);	// 총금액 = 원금 + 가산금 
}
//================== 해결금액 관련 이벤트 처리 END =====================================

//---------- 기간설정 오류 검사 -----------------------
function fn_checkTerm(sYMD, eYMD)
{
	if( length(sYMD) == 0 || length(eYMD) == 0 ) return true;

	if( eYMD < sYMD ){
        gfn_Confirm("MB_OK", "종료년월이 시작년월보다 작습니다.", "경고", "0");
		this.cal_END_YRMN.SetFocus();
		return false;
	}

	return true;
}

/*  ------------------------------
	 두날짜사이의 개월수 계산함수 		
	------------------------------- */
function fn_monthsBetween(sYMD, eYMD)
{	
	var y_cnt 	 = parseInt(substr(eYMD,0,4)) - parseInt(substr(sYMD,0,4)) ; // 년도차 계산
	var m_months = parseInt(substr(eYMD,4,2)) - parseInt(substr(sYMD,4,2)) ; // 월차 계산
	var y_months = y_cnt * 12;
	var r_months = y_months + m_months ;		// 차이월수
	r_months = abs(r_months) + 1 ;				// 차이월수의 양수값 + 1 

	return r_months;
}


//================== 고소메모 관련 이벤트 처리 START =====================================
//고소메모 추가
function btn_add_OnClick(obj)
{
	if( ds_accu_detail.count == 0 ) {
		return;
	}


	ds_accu_memo.AddRow();	
	// 고소정보 상세 ds로부터 고소번호 정보 취득 및 설정
	ds_accu_memo.SetColumn(ds_accu_memo.currow, "ACCU_DAY", ds_accu_detail.GetColumn(ds_accu_detail.currow,"ACCU_DAY"));
	ds_accu_memo.SetColumn(ds_accu_memo.currow, "ACCU_NUM", ds_accu_detail.GetColumn(ds_accu_detail.currow,"ACCU_NUM"));
	ds_accu_memo.SetColumn(ds_accu_memo.currow, "ACCU_BRAN", ds_accu_detail.GetColumn(ds_accu_detail.currow,"ACCU_BRAN"));	

	//신규 추가시 MEMO_NUM의 최대값 찾아서 추가
	var next_memo_num = toInteger(ds_accu_memo.Max("MEMO_NUM")) + 1;
	ds_accu_memo.SetColumn(ds_accu_memo.currow, "MEMO_NUM", next_memo_num);	
	ds_accu_memo.SetColumn(ds_accu_memo.currow, "MEMO_DAY", today());	
}

//고소메모 삭제
function btn_del_OnClick(obj)
{	
	if(ds_accu_memo.currow == -1 ) return;
	// 선택된 고소메모 삭제
	if(gfn_Confirm("MB_YESNO", "선택된 고소메모를 삭제합니다\n삭제하시겠습니까?", "경고", "0") == "6"){
		ds_accu_memo.DeleteRow(ds_accu_memo.currow);
		gfn_SyncCall("accu_mng","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_memo_mng",
		"S=ds_accu_memo:U","ds_accu_memo=SEL10","","fn_CallBack", gv_TraceLog, GV_FORMID);
//		gfn_SyncCall("accu_delete","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_mng",
//		"S=ds_accu_detail:U S1=ds_accu_memo:U","ds_accu_detail=SEL42 ds_accu_memo=SEL45","","fn_CallBack", gv_TraceLog, GV_FORMID);
	}

}
//================== 고소메모 관련 이벤트 처리 END =====================================

//================== 팝업 관련 이벤트 처리 START =====================================
//------------ 업소코드 팝업 함수-----------------
function fn_FindUPSO()
{
	if(length(this.edt_upso_cd.Text) > 0){
		if(gfn_Confirm("MB_YESNO", "데이터가 없습니다.\n새로운 업소를 등록하겠습니까?") == "6"){
			this.ds_upso_info.ClearData();
			this.ds_accu_detail.ClearData();
			this.ds_accu_detail.ClearData();
			var iRow = ds_upso_info.InsertRow(0);
			ds_upso_info.SetColumn(iRow,"CODE_CD","");
			ds_upso_info.SetColumn(iRow,"CODE_NM","");
		}
		else{
			return ;
		}
	}

	if(this.cob_BRAN_CD.Value == ""){
        gfn_Confirm("MB_OK", "센터를 선택해주세요.", "경고", "0");
		return false;
	}

	var BRAN_CD = this.cob_BRAN_CD.Value;
	var UPSO_CD;
	if(length(this.edt_UPSO_NM.Text) == 0){
		UPSO_CD = this.edt_UPSO_CD.Text;
	}
	else{
		UPSO_CD = "";
	}

	this.edt_UPSO_NM.SetFocus();
	var UPSO_NM = this.edt_UPSO_NM.Text;
	//var inParam ="BRAN_CD=" + quote(BRAN_CD) + " UPSO_CD=" + quote(UPSO_CD) + " UPSO_NM=" + quote(UPSO_NM) + " SGBN=" + quote("bra01_s01");
	var inParam ="BRAN_CD=" + quote(BRAN_CD) + " UPSO_CD=" + quote(UPSO_CD) + " UPSO_NM=" + quote(UPSO_NM) + " SGBN=" + quote("bra05_s03");
	var result = Dialog("fi_bra::find_upso.xml",inParam);

	if(0 < length(result)){
	    var vArr = result.split(",");
	    this.edt_UPSO_CD.Text = vArr[0];
	    this.edt_UPSO_NM.Text = vArr[1];
	}	
}

/*
	 * 기      능 : 센터값 가져 오기
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function edt_UPSO_CD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{	
	if(this.cob_BRAN_CD.Index < 0){
        gfn_Confirm("MB_OK", "센터를 선택하십시요", "경고", "0");
		this.cob_BRAN_CD.SetFocus();
		return false;
	}
	//F1 키 눌렸을 대 업소찾기 팝업 표시
	if (nChar == "112"){
		fn_FindUPSO();	
	}

	if (nChar == "13"){
		fn_GetUPSO_CD();

	}
}
/*
	 * 기      능 : 	업소 코드 구하기
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function fn_GetUPSO_CD()
{
	var BRAN_CD = this.cob_BRAN_CD.Value;
	var UPSO_CD = this.edt_UPSO_CD.Text;
	var UPSO_NM = this.edt_UPSO_NM.Text;
	var result = gfn_GetUPSO_CD(BRAN_CD, UPSO_CD, UPSO_NM);

	if (length(result) > 0) {
		var vArr = result.split(",");
		this.edt_UPSO_CD.Text = vArr[0];
		this.edt_UPSO_NM.Text = vArr[1];
		fn_NewInit();
	}
	else {

		fn_FindUPSO();
	}
}


//------------ 경찰서 코드/판결코드 팝업 함수-----------------
function fn_FindAccuCode(KEY_CHAR, CODE_GBN, CODE)
{
	if(length(CODE) > 0 ){
		if(fn_directFindAccu(CODE_GBN, CODE)){		// 정상처리면 리턴
			return;
		}
	}
	//입력된 코드가 없거나 코드에 맞는 결과가 존재하지 않으면 팝업 표시
	CODE = "";
	var inParam = "CODE_GBN=" + quote(CODE_GBN) + " CODE=" + quote(CODE);
	var result = Dialog("fi_bra::find_accu_code.xml",inParam);
	if(0 < length(result)){
	}
}

//------------ 경찰서 코드/판결코드 직접 조회 함수-----------------
function fn_directFindAccu(CODE_GBN, CODE){
	//초기화 => 검색조건과 결과값을 받는 데이터 셋을 같이 쓰므로 
	//          반드시 useClientLayout을 true로 설정 필요
	ds_find_code.ClearData();
	ds_find_code.AddRow();
	ds_find_code.SetColumn(0, "CODE_GBN", CODE_GBN);
	ds_find_code.SetColumn(0, "CODE_CD", CODE);

	gfn_SyncCall("find_code","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=p_plcst_judg","S=ds_find_code","ds_find_code=MRG5","","fn_CallBack");

	if( ds_find_code.count == 0 ) return false;		// 찾은 결과가 없으면 false return

	if( CODE_GBN == "P" ){			// 경찰서 코드
		this.edt_plcst_nm.Text = ds_find_code.GetColumn(0,"CODE_NM");
	}else if ( CODE_GBN == "J" ){	// 판결 코드
	//@@
	}

	return true;
}

