	ubi0.setJrfFileDir(gv_ReportUrl+"fi/bra/");
	ubi0.setJrfFileName("bra05_s03_p4.jrf");

	if(toNumber(length(this.ds_accu_cancel_print2.GetColumn(0, "BRAN_NM")))==17){
		var temp = this.ds_accu_cancel_print2.GetColumn(0, "BRAN_NM");
		temp_1 = substr(temp,0,1)+substr(temp,2,1)+substr(temp,4,1)+substr(temp,6,1)+substr(temp,8,1)+substr(temp,10,1)+substr(temp,12,1)+substr(temp,14,1)+substr(temp,16,1);
		this.ds_accu_cancel_print2.SetColumn(0,"BRAN_NM",temp_1);
	}	
	ubi0.SetDataset("ds_accu_cancel_print2", "ds_accu_cancel_print2");
	ubi0.Retrieve();
	ubi0.directPrint();
}


function ds_accu_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    //btn_cancel
    //btn_statement
    if(strColumnID == "JUDG_CD"){
        if(ds_accu_detail.GetColumn(nRow,"JUDG_CD") == ""){
            cal_COMPN_DAY.Enable = false;
            cal_SOL_START.Enable = false;
            cal_SOL_END.Enable = false;
            cob_PERT_GBN.Enable = false;

            cal_COMPN_DAY.BKColor = "";
            cal_SOL_START.BKColor = "";
            cal_SOL_END.BKColor = "";
            cob_PERT_GBN.BKColor = "";

        }else{
            cal_COMPN_DAY.Enable = true;
            //cal_SOL_START.Enable = true;
            cal_SOL_END.Enable = true;
            cob_PERT_GBN.Enable = true;
        }
    }
	if(strColumnID == "JUDG_CD" || strColumnID == "COMPN_DAY2"){
//	       cal_COMPN_DAY.BKColor = "User13";
//	       cal_SOL_START.BKColor = "User13";
//	       cal_SOL_END.BKColor = "User13";
	    }else{
	        cal_COMPN_DAY.BKColor = "";
	        cal_SOL_START.BKColor = "";
	        cal_SOL_END.BKColor = "";
	        cob_PERT_GBN.BKColor = "";
	    }	    
	}
}

