/*
	 * 기      능 : 	업소 원장조회에서  인자값을 가지고 현 페이지를 호출 시에 가져온 인자 값을 인수로 서치한다.
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function bra05_s03_OnActivate(obj)
{
	if(length(g_bran_cd) == 0 || length(g_upso_cd) == 0){
        return;
    }

	gfn_SetPermission(g_bran_cd);
	gfn_SetInnerPermission("btn_low,btn_attorney,btn_cancel,btn_statement,btn_add,btn_del,btn_calc,btn_memo_save");

	this.cob_BRAN_CD.Value   = g_bran_cd;

	if (length(g_accu_day) > 0 || length(g_accu_num) > 0 || length(g_accu_bran) > 0) {
		this.edt_UPSO_CD.Text    = g_upso_cd;
		this.mae_accu_day.Value  = g_accu_day;
		this.mae_accu_num.Value  = g_accu_num;
		this.edt_accu_bran.Value = g_accu_bran;
		this.cob_accu_gbn.Value  = g_accu_gbn;

		result= gfn_GetUPSO_CD(g_bran_cd, g_upso_cd, "");

		var vArr = result.split(",");
		this.edt_UPSO_CD.Text = vArr[0];
		this.edt_UPSO_NM.Text = vArr[1];
		fn_search();
	}else{
		if(length(g_upso_cd) >0)
		{
			result= gfn_GetUPSO_CD(g_bran_cd, g_upso_cd, "");

			var vArr = result.split(",");
			this.edt_UPSO_CD.Text = vArr[0];
			this.edt_UPSO_NM.Text = vArr[1];

			fn_New();
		}
	}

	g_bran_cd   = "";
    g_upso_cd   = "";
	g_accu_day  = "";
	g_accu_num  = "";
	g_accu_bran = "";
	g_accu_gbn  = "";
}

/*
	 * 기      능 : 	다른 페이지에서 인자값을 가지고 현 페이지를 호출 시 해당페이지가 열려있어도 재실행시켜준다.
	 * 인      수 : 	
	 * Return    : 	
	 * 예      시 :	
*/
function bra05_s03_OnFocus(obj)
{
//trace("bra05_s03_OnFocus " + g_bran_cd + "/" + g_upso_cd + "/" + this.edt_UPSO_CD.Text) ;
	if(length(g_bran_cd) > 0 && this.edt_UPSO_CD.Text != g_upso_cd)
	{
		this.cob_BRAN_CD.Value = g_bran_cd;

//trace("bra05_s03_OnFocus " + g_accu_day + "/" + g_accu_num + "/" + g_accu_bran) ;
		if (length(g_accu_day) > 0 || length(g_accu_num) > 0 || length(g_accu_bran) > 0) {
			this.edt_UPSO_CD.Text = g_upso_cd;
			this.mae_accu_day.Value  = g_accu_day;
			this.mae_accu_num.Value  = g_accu_num;
			this.edt_accu_bran.Value = g_accu_bran;
			this.cob_accu_gbn.Value  = g_accu_gbn;

			result= gfn_GetUPSO_CD(g_bran_cd, g_upso_cd, "");

			var vArr = result.split(",");
			this.edt_UPSO_CD.Text = vArr[0];
			this.edt_UPSO_NM.Text = vArr[1];
			fn_search();
		}

		g_bran_cd = "";
		g_upso_cd = "";
		g_accu_day  = "";
		g_accu_num  = "";
		g_accu_bran = "";
		g_accu_gbn  = "";
    }

    if(length(ds_state_search.GetColumn(0, "LNK_KEY")) > 0)
		gfn_syncCall("SEARCH_STAT","KOMCA?SYSID=PATHFINDER&MENUID=1000002002005&EVENTID=sel_gw_apprv_state","S=ds_state_search","ds_approval_result=SEL1","","fn_CallBack");
}


function edt_upso_cd_OnChanged(obj,strText)
{
	if (length(strText) == 0) {
		this.edt_UPSO_NM.Text = "";
	}
}

function edt_upso_cd_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	var strtext = right(strPostText,1);
	var chkstr = "[<!#$%*&_:=|`~'"+Quote("")+";@^+\\>-]"; 

	if(pos(chkstr, strtext) >= 0)   // 특수문자 사용 제한
	{
		return false;
	}

	if (length(strPostText) == 8 && nChar >= asc('a') && nChar <= asc('z')) {
		obj.Text = strPreText + toUpper(chr(nChar));
		return false;
	}
}


function fn_GetPLST_CD()
{
	var code_gbn = "";
	if(this.cob_accu_gbn2.Index==0)	code_gbn = "P";		// 형사
	else							code_gbn = "L";		// 민사

	this.ds_cd_search.SetColumn(0, "CODE_GBN", code_gbn);
	this.ds_cd_search.SetColumn(0, "CODE_CD" , this.edt_plcst_cd.Text);

	this.edt_plcst_nm.Text = "";

	gfn_SyncCall("svcPLST_CD", "KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=p_plcst_judg", "S=ds_cd_search",
			"ds_cd_info=MRG5", "", "fn_CallBack");	

	if (this.ds_cd_info.rowcount == 1) {
		this.edt_plcst_nm.Text = this.ds_cd_info.GetColumn(0, "CODE_NM");
	}
	else {
		var inParam = "CODE_GBN=" + code_gbn + " CODE=" + quote(this.edt_plcst_CD.Text);
		Dialog("fi_bra::find_accu_code.xml",inParam);
	}
}

function btn_memo_save_OnClick(obj)
{
	if(ds_accu_memo.currow == -1 ) return;
	// 선택된 고소메모 저장
	if(LENGTH(ds_accu_detail.GetColumn(0,"ACCU_NUM")) == 0) {
		ALERT("고소정보가 등록되지 않았습니다.");
		RETURN;
	}
	if(gfn_Confirm("MB_YESNO", "고소메모를 저장합니다\n저장하시겠습니까?", "확인", "0") == "6"){
		gfn_SyncCall("accu_mng","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_memo_mng",
		"S=ds_accu_memo:U","ds_accu_memo=SEL10","","fn_CallBack", gv_TraceLog, GV_FORMID);
	}	
}

function btn_Kian_OnClick(obj)
{
	if((gds_sessioninfo.GetColumn(0, "STAFF_NUM") == ds_accu_detail.GetColumn(0, "STAFF_CD"))
	  || (gds_sessioninfo.GetColumn(0, "DEPT_CD") == "122040100"))
	{
		if(length(ds_accu_detail.GetColumn(0, "JUDG_CD")) > 0)
		{
			//https://group.komca.or.kr/customkomcaeap/sso?compSeq=10170&eapCode=FB0000&lnkKey=20190507-0002-F&loginId=K150009&deptSeq=122040100
			var data1 = "eapCode=FB0000";

			data1 += "&lnkKey=" + ds_accu_detail.GetColumn(0, "ACCU_DAY") + "-" + lpad(ds_accu_detail.GetColumn(0, "ACCU_NUM"), 0, 4)
				  + "-" + ds_accu_detail.GetColumn(0, "ACCU_BRAN") + "&loginId=" + gds_sessioninfo.GetColumn(0, "STAFF_NUM")
				  + "&deptSeq=" + gds_sessioninfo.GetColumn(0, "DEPT_CD");
			var URL = gv_GwURL + "&" + data1;

			//ExecBrowser(URL);
			ExecShell(URL);

			//gfn_syncCall("SEARCH_STAT","KOMCA?SYSID=PATHFINDER&MENUID=1000002002005&EVENTID=sel_gw_apprv_state","S=ds_state_search","ds_approval_result=SEL1","","fn_CallBack");
		}
		else
		{
			alert("판결내역이 존재하지 않습니다.\n저장 후 결재올려주세요.");
		}
	}
	else
	{
		alert("담당자만 결재요청할 수 있습니다.");
		return;
	}
}

function edt_Prodcds_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 112 )
	{
		var ddl = Dialog("fi_pub::com01_r03.xml", "formType=1 prodVal='"+edt_Prod.Text+"'"
			+ " dsName=ds_accu_detail prNrow=0 match=Y colNm1=PROD_CD colNm2=PROD_NM prodttl='"+edt_Prodnm.Text+"'", "");
	}	
}

function btn_evtd_add_OnClick(obj)
{
	//if(ds_accu_detail.rowcount < 1) { return; }
	if(length(ds_evtd_attch.GetColumn(ds_evtd_attch.currow, "FILE_NM")) < 1 && ds_evtd_attch.rowcount > 0) { return; }

	ds_evtd_attch.AddRow();
}

var vObjId = "";

function btn_attch_up_OnClick(obj)
{
	if(ds_accu_detail.rowcount < 1) { return; }

	vObjId = obj.ID;
	gfn_fileUpSelect(20480000,File0,FileDialog0,"setFileUploadData","","ds_boarddataset");
}

function setFileUploadData(path,filename,filesize)
{
    gfn_fileUpLoad("dsUploadFile","tr_result","ds_boarddataset");
}

function tr_result(serid,errid,errmsg)
{
	if(serid == "fileup")
	{
		var filerout 	= dsUploadFile.getcolumn(0,"PATH"); //업로트임시파일위치
		var filenm 	 	= dsUploadFile.getcolumn(0,"UNIFILENAME"); //timestamp파일명
		var upfilename 	= dsUploadFile.getcolumn(0,"UPFILENAME"); //원본파일명

		if(vObjId == "btn_advt_up")
		{
			edt_advt.Text = upfilename;
			if(ds_advt_attch.rowcount < 1)
			{
				ds_advt_attch.AddRow();
			}
			ds_advt_attch.SetColumn(0, "FILE_NM", upfilename);
			ds_advt_attch.SetColumn(0, "FILE_TYPE", "ADVT");
			ds_advt_attch.SetColumn(0, "SVR_FILE_NM", filenm);
			ds_advt_attch.SetColumn(0, "SVR_FILE_ROUT", filerout);

			gfn_SyncCall("svcSave_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_advt_attch:U","", "", "fn_CallBack");
		}
		else if(vObjId == "btn_bpap_up")
		{
			edt_bpap.Text = upfilename;
			if(ds_bpap_attch.rowcount < 1)
			{
				ds_bpap_attch.AddRow();
			}
			ds_bpap_attch.SetColumn(0, "FILE_NM", upfilename);
			ds_bpap_attch.SetColumn(0, "FILE_TYPE", "BPAP");
			ds_bpap_attch.SetColumn(0, "SVR_FILE_NM", filenm);
			ds_bpap_attch.SetColumn(0, "SVR_FILE_ROUT", filerout);

			gfn_SyncCall("svcSave_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_bpap_attch:U","", "", "fn_CallBack");
		}
		else if(vObjId == "btn_comp_up")
		{
			edt_comp.Text = upfilename;
			if(ds_comp_attch.rowcount < 1)
			{
				ds_comp_attch.AddRow();
			}
			ds_comp_attch.SetColumn(0, "FILE_NM", upfilename);
			ds_comp_attch.SetColumn(0, "FILE_TYPE", "COMP");
			ds_comp_attch.SetColumn(0, "SVR_FILE_NM", filenm);
			ds_comp_attch.SetColumn(0, "SVR_FILE_ROUT", filerout);

			gfn_SyncCall("svcSave_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_comp_attch:U","", "", "fn_CallBack");
		}		
		else if(vObjId == "grd_evtd")
		{
			ds_evtd_attch.SetColumn(ds_evtd_attch.currow, "FILE_NM", upfilename);
			ds_evtd_attch.SetColumn(ds_evtd_attch.currow, "FILE_TYPE", "EVTD");
			ds_evtd_attch.SetColumn(ds_evtd_attch.currow, "SVR_FILE_NM", filenm);
			ds_evtd_attch.SetColumn(ds_evtd_attch.currow, "SVR_FILE_ROUT", filerout);

			gfn_SyncCall("svcSave_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_evtd_attch:U","", "", "fn_CallBack");
		}

		vObjId = "";
	}
}

function btn_attch_down_OnClick(obj)
{
	var rout = "";
	var file = "";
	var file_org = "";

	if(obj.ID == "btn_advt_down")
	{
		rout = ds_advt_attch.GetColumn(0, "SVR_FILE_ROUT");
		file = ds_advt_attch.GetColumn(0, "SVR_FILE_NM");
		file_org = ds_advt_attch.GetColumn(0, "FILE_NM");
	}
	else if(obj.ID == "btn_bpap_down")
	{
		rout = ds_bpap_attch.GetColumn(0, "SVR_FILE_ROUT");
		file = ds_bpap_attch.GetColumn(0, "SVR_FILE_NM");
		file_org = ds_bpap_attch.GetColumn(0, "FILE_NM");
	}
	else if(obj.ID == "btn_comp_down")
	{
		rout = ds_comp_attch.GetColumn(0, "SVR_FILE_ROUT");
		file = ds_comp_attch.GetColumn(0, "SVR_FILE_NM");
		file_org = ds_comp_attch.GetColumn(0, "FILE_NM");
	}	
	else if(obj.ID == "grd_evtd")
	{
		rout = ds_evtd_attch.GetColumn(ds_evtd_attch.currow, "SVR_FILE_ROUT");
		file = ds_evtd_attch.GetColumn(ds_evtd_attch.currow, "SVR_FILE_NM");
		file_org = ds_evtd_attch.GetColumn(ds_evtd_attch.currow, "FILE_NM");
	}

	gfn_fileDownLoad(rout, file, File0, FileDialog0, file_org);	//다운로드일때
}

function btn_attch_del_OnClick(obj)
{
	if(!Confirm("삭제하시겠습니까?")) { return; }

	if(obj.ID == "btn_advt_del")
	{
		ds_advt_attch.DeleteRow(0);
		gfn_SyncCall("svcDel_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_advt_attch:U","", "", "fn_CallBack");
	}
	else if(obj.ID == "btn_bpap_del")
	{
		ds_bpap_attch.DeleteRow(0);
		gfn_SyncCall("svcDel_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_bpap_attch:U","", "", "fn_CallBack");
	}
	else if(obj.ID == "btn_evtd_del")
	{
		if(length(ds_evtd_attch.GetColumn(ds_evtd_attch.currow, "FILE_NM")) < 1 && ds_evtd_attch.rowcount > 0)
		{
			ds_evtd_attch.DeleteRow(ds_evtd_attch.currow);
		}
		else
		{
			ds_evtd_attch.DeleteRow(ds_evtd_attch.currow);
			gfn_SyncCall("svcDel_file","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=mng_accu_attch","S=ds_accu_detail S1=ds_evtd_attch:U","", "", "fn_CallBack");
		}
	}

}

function grd_evtd_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if(nCell == grd_evtd.GetBindCellIndex("body", "UP"))
	{
		btn_attch_up_OnClick(obj);
	}
	else if(nCell == grd_evtd.GetBindCellIndex("body", "DOWN"))
	{
		btn_attch_down_OnClick(obj);
	}
}

function edt_accu_bran_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	var strtext = right(strPostText,1);
	var chkstr = "[<!#$%*&_:=|`~'"+Quote("")+";@^+\\>-]"; 

	if(pos(chkstr, strtext) >= 0)   // 특수문자 사용 제한
	{
		return false;
	}

	if (nChar >= asc('a') && nChar <= asc('z')) {
		obj.Text = toUpper(chr(nChar));
		return false;
	}
}	

function btn_accu_del_OnClick(obj){

	if(confirm("고소번호 " + this.mae_accu_day_2.Value + "-" + this.mae_accu_num_2.Value + "-" + this.edt_accu_bran_2.Text + "를 삭제하시겠습니까?")){
		ds_delete.SetColumn(0, "ACCU_DAY", this.mae_accu_day_2.Value);
		ds_delete.SetColumn(0, "ACCU_NUM", this.mae_accu_num_2.Value);
		ds_delete.SetColumn(0, "ACCU_BRAN", this.edt_accu_bran_2.Text);
		//trace(ds_delete.SaveXML());

		gfn_SyncCall("svcDelete","KOMCA?SYSID=PATHFINDER&MENUID=1000001006002001&EVENTID=accu_delete", "S=ds_delete","","","fn_CallBack");
		//trace("trace");
	}
	return;
}
