//	공통버튼 컨트롤
function fn_CommmonButtonClick(strCode){
    //신규
    if(strCode == "NEW"){}
    //조회
    else if(strCode == "SEARCH"){
		fn_Search();
    }
    //저장
    else if(strCode == "SAVE"){}    
    //삭제
    else if(strCode == "DELETE"){}
    //Excel
    else if(strCode == "EXCEL"){
		fn_Excel();
    }
    //출력
    else if(strCode == "PRINT"){}
    //닫기
    else if(strCode == "CLOSE"){
        close();
    }	return;}

//조회
function fn_Search()
{
    if (!gfn_CheckFormNull(Div0)) return;
    
    if(!gfn_changeAction("SEARCH", "ds_list")) return;
    trace(this.ds_search.SaveXML());
    //ds_search.SetColumn(0,"ISSUE_YRMN","201005");
    ds_search.SetColumn(0, "START_DAY", cal_startdate.Value);
    ds_search.SetColumn(0, "END_DAY", cal_enddate.Value);
    
	if(parseInt(cal_enddate.Value) < parseInt("20181130"))
    {
		gfn_syncCall("search_sms_list","KOMCA?SYSID=PATHFINDER&MENUID=1000001006004006002&EVENTID=search_sms_list","S=ds_search","ds_list=SEL1","","fn_CallBack", gv_TraceLog, gv_formid);
		grd_result.Visible = true;
		grd_kakao_result.Visible = false;
		vObject = ds_list;
    }
    else
    {
		gfn_syncCall("search_sms_list","KOMCA?SYSID=PATHFINDER&MENUID=1000001006004006002&EVENTID=log_kakao_msg","S=ds_search","ds_list2=SEL1","","fn_CallBack", gv_TraceLog, gv_formid);
		grd_result.Visible = false;
		grd_kakao_result.Visible = true;
		vObject = ds_list2;
    }
    
    //grd_result.Format = "default0";
}


