//CallBack
function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0) {
		//에러 공통 팝업
		gfn_SetErrorPop(ErrorCode,ErrorMsg);
		return;
	}
	switch(srvId)
	{
		case "svcInsIndtn" :
			if(parseInt(ds_cnt.GetColumn(0, "CNT")) > 0)
			{
				alert("MICR등록이 처리되었습니다.");
			}
			else
			{
				alert("선택된 리스트가 없거나, MICR 등록에 실패했습니다.");
			}
			break;
		default :
			break;
	}
}

//센터콤보박스가 변경되었을때
function cob_BRAN_CD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex){
	gfn_SetPermission(strCode);

	this.ds_BRAN_CD.SetColumn(0,"BRAN_CD", this.Div0.cob_BRAN_CD.value);
	gfn_syncCall("SAWON_CLICK","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select","S=ds_BRAN_CD","ds_SawonInfo=SEL1","","fn_CallBack");
	var iRow = ds_SawonInfo.InsertRow(0);
	ds_SawonInfo.SetColumn(iRow,"CODE_CD","");
	ds_SawonInfo.SetColumn(iRow,"CODE_NM","");

	this.cob_Staff_Nm.Value = "";
	this.cob_Staff_Nm.Enable = true;
}


function grd_result_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(!Confirm("재전송 하시겠습니까?")) return;
	
	gds_kakao.ClearData();
	
	fn_make_msg(vObject.currow);

	var rtn = gfn_ChkKakao();
	if(rtn)
	{
		gfn_SyncCall("svcSendKakao","KOMCA?SYSID=PATHFINDER&MENUID=1000006001010&EVENTID=send_kakao_msg","S=gds_kakao:U","","","gfn_CallBack");
	}
}

function btn_resend_OnClick(obj)
{
	if(!Confirm("재전송 하시겠습니까?")) return;
	
	gds_kakao.ClearData();
	
	for(var i = 0; i < vObject.rowcount; i++)
	{
		if(vObject.GetColumn(i, "CHK") == "1")
		{
			fn_make_msg(i);
		}
	}
	
	var rtn = gfn_ChkKakao();
	if(rtn)
	{
		gfn_SyncCall("svcSendKakao","KOMCA?SYSID=PATHFINDER&MENUID=1000006001010&EVENTID=send_kakao_msg","S=gds_kakao:U","","","gfn_CallBack");
	}
}

function cal_OnChanged(obj,strOldText,strNewText)
{
	if(obj == cal_startdate)
	{
		if(cal_startdate.Value > cal_enddate.Value)
			cal_enddate.Value = cal_startdate.Value;
		
		if(parseInt(cal_enddate.Value) >= parseInt("20181130") && parseInt(cal_startdate.Value) < parseInt("20181130"))
			cal_enddate.Value = cal_startdate.Value;
	}
	else if(obj == cal_enddate)
	{
		if(cal_startdate.Value > cal_enddate.Value)
			cal_startdate.Value = cal_enddate.Value;
			
		if(parseInt(cal_enddate.Value) >= parseInt("20181130") && parseInt(cal_startdate.Value) < parseInt("20181130"))
			cal_startdate.Value = cal_enddate.Value;
	}
}

function fn_make_msg(vRow)
{
	var v_phone_num = "";
	var v_subject = "";
	var v_snd_msg = "";
	var v_sms_snd_num = "";
	var v_reserved1 = "";
	var v_reserved2 = "";
	var v_reserved3 = "";
	var v_reserved6 = "";
	var v_upso_cd = "";
	
	if(parseInt(cal_enddate.Value) < parseInt("20181130"))
	{
		v_phone_num   = ds_list.GetColumn(vRow, "PHONE_NUMBER");
		v_subject     = "(사)한국음악저작권협회";
		v_snd_msg     = ds_list.GetColumn(vRow, "SMS_MSG");
		v_sms_snd_num = ds_list.GetColumn(vRow, "BRAN_PHON_NUM");
		v_reserved1   = ds_list.GetColumn(vRow, "UPSO_CD");
		v_reserved2   = ds_list.GetColumn(vRow, "DEST_NAME");
		v_reserved3   = ds_list.GetColumn(vRow, "BRAN_CD");
		v_upso_cd     = ds_list.GetColumn(vRow, "UPSO_CD");
	}
	else
	{
		v_phone_num   = ds_list2.GetColumn(vRow, "PHONE_NUM");
		v_subject     = ds_list2.GetColumn(vRow, "SUBJECT");
		v_snd_msg     = ds_list2.GetColumn(vRow, "SND_MSG");
		v_sms_snd_num = ds_list2.GetColumn(vRow, "SMS_SND_NUM");
		v_reserved1   = ds_list2.GetColumn(vRow, "RESERVED1");
		v_reserved2   = ds_list2.GetColumn(vRow, "RESERVED2");
		v_reserved3   = ds_list2.GetColumn(vRow, "RESERVED3");
		v_upso_cd     = ds_list2.GetColumn(vRow, "UPSO_CD");
	}
	
	v_reserved6 = "bra06_r14";
	
	var aRow = gds_kakao.AddRow();
	gds_kakao.SetColumn(aRow, "PHONE_NUM",   v_phone_num); //수신자번호
	gds_kakao.SetColumn(aRow, "SUBJECT",     v_subject); //문자제목
	gds_kakao.SetColumn(aRow, "SND_MSG",     v_snd_msg); //내용
	gds_kakao.SetColumn(aRow, "SMS_SND_NUM", v_sms_snd_num); //발신자번호
	gds_kakao.SetColumn(aRow, "RESERVED1",   v_reserved1); //업소코드
	gds_kakao.SetColumn(aRow, "RESERVED2",   v_reserved2); //업주명
	gds_kakao.SetColumn(aRow, "RESERVED3",   v_reserved3);
	gds_kakao.SetColumn(aRow, "RESERVED4",   "");
	gds_kakao.SetColumn(aRow, "RESERVED5",   "");
	gds_kakao.SetColumn(aRow, "RESERVED6",   v_reserved6); //form id
	gds_kakao.SetColumn(aRow, "USER_ID",     v_upso_cd); //업소코드
}

function grd_result_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell == obj.GetBindCellIndex("body", "CHK")) {
		var check = !(obj.GetCellProp("head", nCell, "expr"));

		obj.SetCellProp("head", nCell, "expr", check);
		
		for(var i = 0; i <vObject.rowcount; i++)
		{
			vObject.SetColumn(i, "CHK", check);
		}
	}
	else
	{
		gfn_GridSort(obj, nCell);
	}
}

function btn_ins_indtn_OnClick(obj)
{
	if(!Confirm("선택된 업소를 MICR로 등록 하시겠습니까?")) return;
	
	ds_cnt.ClearData();
	ds_indtn.ClearData();
	
	for(var i = 0; i < vObject.rowcount; i++)
	{
		if(vObject.GetColumn(i, "CHK") == "1")
		{
			var j = ds_indtn.AddRow();
			ds_indtn.SetColumn(j, "UPSO_CD", vObject.GetColumn(i, "UPSO_CD"));
		}
	}
	
	gfn_SyncCall("svcInsIndtn","KOMCA?SYSID=PATHFINDER&MENUID=1000001006005005&EVENTID=insert_indtn","S=ds_indtn","ds_cnt=ADD11","","fn_CallBack");
}
