#include "script::common.js"  	// 공통 js
#include "script::global.js"  	// 공통 js
#include "script::commonGibu.js"  	// 센터 공통 js

var end_flag=0; // 마감 데이터 구분 , 0=현황데이터 / 1=마감데이터

//	공통버튼 컨트롤
function fn_CommmonButtonClick(strCode){
    //신규
    if(strCode == "NEW"){
		fn_new();
    }
    //조회
    else if(strCode == "SEARCH"){
		fn_Search();
    }
    //저장
    else if(strCode == "SAVE"){
		fn_Save();
    }    
    //삭제
    else if(strCode == "DELETE"){}
    //Excel
    else if(strCode == "EXCEL"){
		fn_Excel();
    }
    //출력
    else if(strCode == "PRINT"){}
    //닫기
    else if(strCode == "CLOSE"){
        close();
    }	return;}


function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0) {
		//에러 공통 팝업
		gfn_SetErrorPop(ErrorCode,ErrorMsg);
	}
	
	if (srvId == "gov_apprv_save") {
		alert("저장되었습니다.");
	}
	else if (srvId == "gov_end_save") {
		alert(substr(today(),0,6)+"월 전체 마감 저장되었습니다.");
	}
	else if (svrId == "gov_his_search"){
		alert("마감 데이터 조회되었습니다.");
	}
	else if (srvId == "gov_his_save") {
		alert("마감 수정되었습니다.");
	}
	else if (srvId == "gov_update") {
		alert("데이터가 수정되었습니다.");
	}
}

function bra08_s10_OnLoadCompleted(obj)
{	
	gfn_LoadForm(obj);
	gfn_GetDataGIBU_CD(cob_BRAN_CD,0);	

	ds_TGOV_INTE.ClearData();
	
	cob_BRAN_CD.Index=0;
	
	// 
	cob_apv_dcb.Enable=false;
	cob_match.Enable=false;
	
	if (gds_sessioninfo.GetColumn(0, "BRAN_CD") == "AL" || gds_sessioninfo.GetColumn(0, "DEPT_CD") ="122040100")
	{
		cob_BRAN_CD.Enable=true;
		btn_Save.Visible=true;
	}
	else
	{
		cob_BRAN_CD.Enable=false;
		btn_Save.Visible=false;
	}
	
}

function fn_new()
{
	bra08_s10_OnLoadCompleted(obj);
}

function fn_Search()
{
	ds_search.ClearData();
	ds_search.AddRow();
	ds_search.SetColumn(0,"BRAN_CD",cob_BRAN_CD.Value);
	ds_search.SetColumn(0,"STAFF_CD",cob_Staff_Nm.Value);
	ds_search.SetColumn(0,"GBN",cob_gbn.Value);
	
	//trace(ds_search.SaveXML());
	gfn_syncCall("gov_apprv_search","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008009&EVENTID=gov_apprv_search", "S=ds_search", "ds_TGOV_INTE=SEL1", "", "fn_CallBack");
	cob_apv_dcb.Enable=true;
	cob_match.Enable=true;
	end_flag=0;

}

function cob_BRAN_CD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_BRAN_CD.SetColumn(0,"BRAN_CD", cob_BRAN_CD.Value );
	
	if(cob_BRAN_CD.Value =='AL')
	{	
		ds_SawonInfo.ClearData();
	}
	else
	{
		gfn_SetPermission(cob_BRAN_CD.Value);
		gfn_PubCode('ds_stat_gbn', '2', '00433', 'STAT');
		gfn_syncCall("SAWON_CLICK","KOMCA?SYSID=PATHFINDER&MENUID=1000001006007&EVENTID=p_staff_select","S=ds_BRAN_CD","ds_SawonInfo=SEL1","","fn_CallBack");
		
		var iRow = ds_SawonInfo.InsertRow(0);
		ds_SawonInfo.SetColumn(iRow,"STAFF_CD","");
		ds_SawonInfo.SetColumn(iRow,"STAFF_NM","--ALL--");
		cob_Staff_Nm.Index=0;
	}
	
}

function fn_Save()
{	
	if (gds_sessioninfo.GetColumn(0, "BRAN_CD") <> "AL" && gds_sessioninfo.GetColumn(0, "DEPT_CD") <> "122040100")
	{
		alert("센터관리팀만 저장할 수 있습니다.");
		return;
	}

	if (ds_gov_update.rowcount<=0)
	{
		alert("수정할 데이터가 없습니다.");
		return;	
	}
	
	if(end_flag==0)
	{
		if(!confirm("정부3.0 데이터가 수정됩니다. 저장 하시겠습니까?")){ return; }
		
		for(var i=0; i<ds_gov_update; i++)
		{
			ds_gov_update.SetColumn(i,"GBN_1","1"); 
		}
		
		gfn_syncCall("gov_update","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008009&EVENTID=gov_update","S=ds_gov_update:U","","","fn_CallBack");
	
		fn_Search();		
	}
	else if (end_flag==1)
	{
		if(!confirm("마감 데이터가 수정됩니다. \n 저장하시겠습니까?")){ return; }
		
		for(var i=0; i<ds_gov_update; i++)
		{
			ds_gov_update.SetColumn(i,"GBN_1","2"); 
		}
		
		gfn_syncCall("gov_update","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008009&EVENTID=gov_update","S=ds_gov_update:U","","","fn_CallBack");
		
		fn_History_Search();
	}	
}

function set_Filter()
{
	if(cob_apv_dcb.Value == "APV" && cob_match.Value=="")
	{
		ds_TGOV_INTE.Filter("DCB_YMD = '' ");
	}
	else if(cob_apv_dcb.Value == "DCB"&& cob_match.Value=="" )
	{
		ds_TGOV_INTE.Filter("DCB_YMD <> '' ");
	}
	if(cob_match.Value=="N" && cob_apv_dcb.Value=="")
	{
		ds_TGOV_INTE.Filter("UPSO_CD = '' ");
	}
	else if (cob_match.Value=="Y"&& cob_apv_dcb.Value=="")
	{
		ds_TGOV_INTE.Filter("UPSO_CD <> ''");
	}
	else if (cob_apv_dcb.Value == "APV" && cob_match.Value=="N")
	{
		ds_TGOV_INTE.Filter("UPSO_CD = '' AND DCB_YMD = '' ");
	}
	else if (cob_apv_dcb.Value == "APV" && cob_match.Value=="Y")
	{
		ds_TGOV_INTE.Filter("UPSO_CD <> '' AND DCB_YMD = '' ");
	}
	else if (cob_apv_dcb.Value == "DCB" && cob_match.Value=="N")
	{
		ds_TGOV_INTE.Filter("UPSO_CD = '' AND DCB_YMD<> '' ");
	}
	else if (cob_apv_dcb.Value == "DCB" && cob_match.Value=="Y")
	{
		ds_TGOV_INTE.Filter("UPSO_CD <> '' AND DCB_YMD<> '' ");
	}	
	else if (cob_apv_dcb.Value == "" && cob_match.Value=="")
	{
		ds_TGOV_INTE.UnFilter();
	}
	
}

function fn_History_Search()
{
	ds_history_sc.SetColumn(0,"BRAN_CD",cob_BRAN_CD.Value);
	ds_history_sc.SetColumn(0,"STAFF_CD",cob_Staff_Nm.Value);
	ds_history_sc.SetColumn(0,"GBN",cob_gbn.Value);
	//trace(ds_history_sc.SaveXML());
	gfn_syncCall("gov_his_search","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008009&EVENTID=gov_history_search","S=ds_history_sc","ds_TGOV_INTE=SEL1","","fn_CallBack");
	end_flag=1;
}

function rad_SEARCH_GBN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(rad_SEARCH_GBN.Value == "1")
	{
		btn_history_view.Visible = false;
	}
	else if(rad_SEARCH_GBN.Value == "2")
	{
		btn_history_view.Visible = true;
		
		if(Dialog("fi_bra::pop_gov_history.xml", ""))
		{
			fn_History_Search();
		}
	}
}

function btn_history_view_OnClick(obj,nX,nY)
{
	ds_history_sc.ClearData();
	ds_history_sc.AddRow();
	if(Dialog("fi_bra::pop_gov_history.xml", ""))
	{
		fn_History_Search();
	}
	
}

function btn_Save_OnClick(obj,nX,nY)
{
	// 마감여부 확인
	
	gfn_syncCall("gov_his_search","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008009&EVENTID=gov_history_list","","ds_gov_History=SEL1","","fn_CallBack");
	
	if (ds_gov_History.GetColumn(0,"CONFIRM_YRMN") == substr(today(),0,6) )
	{
		if(!confirm(substr(today(),0,6) +" 마감된 데이터가 있습니다.\n저장하시겠습니까?")){ return; }
	}
	
	// 저장
	gfn_syncCall("gov_end_save","KOMCA?SYSID=PATHFINDER&MENUID=1000001006008009&EVENTID=gov_end_save","","","","fn_CallBack");
	
}

