function upso_visit_OnLoadCompleted(obj)
{

	//this.edt_UPSO_CD.Text = "7203006O";
	//this.edt_UPSO_NM.Text = "십구홀";

	this.edt_UPSO_CD.Text = UPSO_CD;
	this.edt_UPSO_NM.Text = UPSO_NM;

	start_date = "19990101" ;
	end_date = today();

	this.tab_Onwer.MNGEMSTR.cob_match_yn.Value ='M';

	// 설정 코드 정보 가져오기
	gfn_PubCode("ds_gbn", 2, "00198");		//구분
	gfn_PubCode("ds_reas_close", 2, "00198", "CR"); //휴업 첨부파일 타입
	gfn_PubCode("ds_reas_end", 2, "00198", "ER"); //휴업 첨부파일 타입
	ds_gbn.Filter("CODE_ETC=\"G\"");

	//gfn_SetPermission(this.cob_BRAN_CD.value);
    //gfn_SetInnerPermission("tab_Onwer.MNGEMSTR.btn_print,tab_Onwer.MNGEMSTR.btn_play,tab_Onwer.MNGEMSTR.btn_call_bra01_s01,tab_Onwer.MNGEMSTR.btn_add,tab_Onwer.MNGEMSTR.btn_del");
	fn_Search();

	if(tab_Onwer.DOCUMENT.cob_3_gbn.Value == "1") // 휴업
	{
		tab_Onwer.DOCUMENT.cob_3_reas_gbn.InnerDataset = "ds_reas_close";
	}
	else // 폐업
	{
		tab_Onwer.DOCUMENT.cob_3_reas_gbn.InnerDataset = "ds_reas_end";
	}
}

function fn_Search() 
{	
	this.tab_Onwer.MNGEMSTR.edt_Remak.BKColor = "";

	this.tab_Onwer.MNGEMSTR.cob_match_yn.BKColor = "";	//구분은 필수

	if(!gfn_changeAction("SEARCH", "ds_visitInfo,ds_visitBre")) return;

	ds_search.ClearData();
	ds_search.AddRow();
	ds_search.SetColumn(0, "UPSO_CD", this.edt_UPSO_CD.Text);
	ds_search.SetColumn(0, "START_DAY", start_date);
	ds_search.SetColumn(0, "END_DAY", end_date);

	// 초기화 처리
	ds_visitInfo.row = -1;
	this.tab_Onwer.MNGEMSTR.cob_Conspres_Gbn.Index = -1;


	if(this.tab_Onwer.TabIndex==0){
		ds_search.SetColumn(0, "YEAR", substr(getDate(),0,4));
		//gfn_SyncCall("visit_info","KOMCA?SYSID=PATHFINDER&MENUID=1000001006004010&EVENTID=upso_visit_list","S=ds_search","ds_visitInfo=SEL1 ds_upsoInfo=SEL2","","fn_CallBack");
		gfn_syncCall("UPSO_DETAIL","KOMCA?SYSID=PATHFINDER&MENUID=1000001006001008001&EVENTID=upso_rept_select","S=ds_search","ds_upso=SEL1 ds_wonjang=SEL4 ds_history=SEL3 ds_more_amt=SEL12","","fn_CallBack");

		//붉은 색의 좀 더 넣은 금액이 있을 경우 화면에 나타내게 하며 없을 경우는 안 보이게 한다.
		if(ds_more_amt.RowCount()==0){
			this.tab_Onwer.UPSO_NOTE.shape_more.Visible = false;
			this.tab_Onwer.UPSO_NOTE.mae_BALANCE.Visible = false;
		}
		else{
			if (toNumber(this.ds_more_amt.GetColumn(0, "BALANCE")) == 0) {
				this.tab_Onwer.UPSO_NOTE.shape_more.BKColor = "white";
				this.tab_Onwer.UPSO_NOTE.mae_BALANCE.BKColor = "white";
			}
			else {
				this.tab_Onwer.UPSO_NOTE.shape_more.BKColor = "red";
				this.tab_Onwer.UPSO_NOTE.mae_BALANCE.BKColor = "red";
			}
			this.tab_Onwer.UPSO_NOTE.shape_more.Visible = true;
			this.tab_Onwer.UPSO_NOTE.mae_BALANCE.Visible = true;
		}

	//자동이체 여부
		if(this.tab_Onwer.UPSO_NOTE.Edit9.Text != ""){
			this.tab_Onwer.UPSO_NOTE.Edit9.BKColor = "blue";
			this.tab_Onwer.UPSO_NOTE.Edit9.Color = "white";
		}
		else {
			this.tab_Onwer.UPSO_NOTE.Edit9.BKColor = "white";
		} 

	//채권여부
		if(this.tab_Onwer.UPSO_NOTE.edit1.Text != ""){
			this.tab_Onwer.UPSO_NOTE.edit1.BKColor = "blue";
			this.tab_Onwer.UPSO_NOTE.edit1.Color = "white"; 
		}
		else {
			this.tab_Onwer.UPSO_NOTE.edit1.BKColor = "white";
		} 

	//온오프라인데이타
		if(this.tab_Onwer.UPSO_NOTE.edit0.Text = "오프라인"){
			this.tab_Onwer.UPSO_NOTE.edit0.BKColor = "blue";
			this.tab_Onwer.UPSO_NOTE.edit0.Color = "white";
		} else if(substr(this.tab_Onwer.UPSO_NOTE.edit0.Text,0,4) = "무선로그"){
			this.tab_Onwer.UPSO_NOTE.edit0.BKColor = "orange";
			this.tab_Onwer.UPSO_NOTE.edit0.Color = "black";
		} else if(substr(this.tab_Onwer.UPSO_NOTE.edit0.Text,0,7) = "오프,무선로그"){
			this.tab_Onwer.UPSO_NOTE.edit0.BKColor = "yellow";
			this.tab_Onwer.UPSO_NOTE.edit0.Color = "black";
		}
		else {
			this.tab_Onwer.UPSO_NOTE.edit0.BKColor = "white";
			this.tab_Onwer.UPSO_NOTE.edit0.Color = "black";
		} 

	//고소여부 데이타
		if(this.tab_Onwer.UPSO_NOTE.edit7.Text != ""){
			this.tab_Onwer.UPSO_NOTE.edit7.BKColor = "blue";
			this.tab_Onwer.UPSO_NOTE.edit7.Color = "white";
		}
		else {
			this.tab_Onwer.UPSO_NOTE.edit7.BKColor = "white";
		} 

	//	trace(this.ds_upso.SaveXML());

		this.tab_Onwer.UPSO_NOTE.edt_use_amt.Text    = this.ds_wonjang.Sum("USE_AMT");
		this.tab_Onwer.UPSO_NOTE.edt_b_use_amt.Text  = this.ds_wonjang.Sum("B_USE_AMT");
		this.tab_Onwer.UPSO_NOTE.edt_bb_use_amt.Text = this.ds_wonjang.Sum("BB_USE_AMT");
