		this.tab_Onwer.UPSO_NOTE.edt_UPSO_NM.Text	 = this.ds_upso.GetColumn(0,"UPSO_NM");

	//sms 활성화 여부[휴대폰 번호가 있으면 활성화 아니면 비활성화
		/*if(length(this.ds_upso.GetColumn(0,"MNGEMSTR_HPNUM"))==0){
				this.btn_send_sms.Enable = false;
		}
		else	this.btn_send_sms.Enable = true;

		if (length(this.edt_UPSO_CD1.Text) > 0) {
			fn_SetShortCutEnabled(true);
		}
		else {
			fn_SetShortCutEnabled(false);
		}*/
	}
	else if(this.tab_Onwer.TabIndex==1){
		gfn_AsyncCall("visit_info","KOMCA?SYSID=PATHFINDER&MENUID=1000001006004010&EVENTID=upso_visit_list","S=ds_search","ds_visitInfo=SEL1 ds_upsoInfo=SEL2","","fn_CallBack");
