Window.Script:
#include "script::common.js"  	// 공통 js
#include "script::global.js"  	// 공통 js
#include "script::commonGibu.js"  	// 센터 공통 js


function fn_CommmonButtonClick(strCode)
{
    //신규
    if(strCode == "NEW"){
		fn_New();
    }
    //조회
    else if(strCode == "SEARCH"){
		fn_Search();
    }
    //저장
    else if(strCode == "SAVE"){
		fn_Save();
    }
    //삭제
    else if(strCode == "DELETE"){
		fn_Delete();
    }
    //Excel
    else if(strCode == "EXCEL"){
		fn_Excel();
    }
    //출력
    else if(strCode == "PRINT"){
		fn_Print();
    }
    //닫기
    else if(strCode == "CLOSE"){
        close();
    }

	return;
}

function form_OnLoadCompleted(obj)
{
	gfn_syncCall("svc_Search","KOMCA?SYSID=PATHFINDER&MENUID=1000001006012009&EVENTID=staff_clct_history","","ds_Staff_Clct_History=SEL1","","fn_CallBack");
}

function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0) {
		//에러 공통 팝업
		gfn_SetErrorPop(ErrorCode,ErrorMsg);
	}
}

function btn_Close_OnClick(obj)
{
	Close(false);
}

function ikikikik_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	parent.ds_CompIn.SetColumn(0, "YRMN", ds_Staff_Clct_History.GetColumn(ds_Staff_Clct_History.currow, "PRCON_YRMN"));
	parent.ds_CompIn.SetColumn(0, "SEQ", ds_Staff_Clct_History.GetColumn(ds_Staff_Clct_History.currow, "SEQ"));
	parent.ds_CompIn.SetColumn(0, "BRAN_CD", ds_Staff_Clct_History.GetColumn(ds_Staff_Clct_History.currow, "BRAN_CD"));
	close(true);
}
